        [RelayCommand]
        private async Task Refresh()
        {
            if (IsBusy) return;
            try
            {
                IsBusy = true;
                LoadSettings();

                if (_connectivity.NetworkAccess != NetworkAccess.Internet)
                {
                    IsOnline = false;
                    await Shell.Current.DisplayAlert("Không có kết nối mạng!",
                        $"Vui lòng kiểm tra internet và thử lại.", "OK");
                    return;
                }
                else
                {
                    IsOnline = true;
                }

                await _loadingService.ShowLoadingAsync("Tải dữ liệu...");
                StatusForecast = "Đang tải dữ liệu...";
                AgricultureDescription = "Đang tải dữ liệu...";
                StatusClay = "Đang tải dữ liệu...";

                // LẤY DỮ LIỆU THỜI TIẾT TỪ GOOGLE SHEET
                LocalWeathers.Clear();
                var sheets = await _googleSheetsService.GetSheetNamesAsync(ConstantHelper.GoogleSpreadsheetId);
                var sheetDate = Convert.ToDateTime(sheets.FirstOrDefault(x => !x.Contains("Du lịch") && !x.Contains("Thời tiết")));
                foreach (var item in sheets)
                {
                    if (!item.Contains("Du lịch") && !item.Contains("Thời tiết"))
                    {
                        var date = Convert.ToDateTime(item.ToString());
                        if (date > sheetDate) sheetDate = date;
                    }
                }
                if (Preferences.Get(SettingKeys.IsAutoUpdateEnabled, true))
                    Preferences.Set(SettingKeys.LastSourceSheet, sheetDate.ToString("dd/MM/yyyy"));

                string sheetName = Preferences.Get(SettingKeys.LastSourceSheet, sheetDate.ToString("dd/MM/yyyy"));
                var datas = await _googleSheetsService.ReadEntireSheetAsync(ConstantHelper.GoogleSpreadsheetId, sheetName);

                for (int row = 2; row < datas.Count; row++)
                {
                    var diaDiemDuBao = datas[row][0].ToString();
                    var localWeather = new LocalWeather
                    {
                        DiaDiemDuBao = diaDiemDuBao,
                        Currents = []
                    };

                    CurrentLocal current = null!;
                    for (int col = 1; col < datas[row].Count; col++)
                    {
                        var dateCell = datas[0][col].ToString();
                        var headerCell = datas[1][col].ToString();
                        var cellValue = datas[row][col].ToString();

                        if (!string.IsNullOrEmpty(dateCell))
                        {
                            current = new CurrentLocal { ThoiGian = dateCell };
                            localWeather.Currents.Add(current);
                        }

                        if (current == null) continue;
                        switch (headerCell)
                        {
                            case "Tm": current.Tm = int.TryParse(cellValue, out int tm) ? tm : (int?)null; break;
                            case "Tx": current.Tx = int.TryParse(cellValue, out int tx) ? tx : (int?)null; break;
                            case "R": current.R = int.TryParse(cellValue, out int r) ? r : (int?)null; break;
                            case "Xác suất mưa": current.XacSuatMua = int.TryParse(cellValue, out int xsm) ? xsm : (int?)null; break;
                            case "H. gió": current.HuongGio = cellValue; break;
                            case "Tốc độ gió": current.TocDoGio = int.TryParse(cellValue, out int tdg) ? tdg : (int?)null; break;
                            case "Đ.ẩm": current.DoAm = int.TryParse(cellValue, out int dam) ? dam : (int?)null; break;
                            case "T.tiết": current.ThoiTiet = int.TryParse(cellValue, out int tt) ? tt : (int?)null; break;
                            default: break;
                        }
                    }
                    LocalWeathers.Add(localWeather);
                }

                // LỌC DỮ LIỆU THEO HUYỆN ĐANG CHỌN
                if (!string.IsNullOrEmpty(SelectedLocation))
                {
                    SelectedLocalWeather = LocalWeathers.FirstOrDefault(x => x.DiaDiemDuBao!.Equals(SelectedLocation))!;
                    CurrentLocals = new(SelectedLocalWeather.Currents!);
                    StatusForecast = "Nguồn: Đài khí tượng thủy văn tỉnh Ninh Thuận";
                }

                // LẤY DỮ LIỆU THỜI TIẾT TỪ TOMORROW CỦA NINH THUẬN
                Current = await _tomorrowService.GetCurrentWeatherAsync("Ninh Thuận", ConstantHelper.TomorrowApiKey);

                if (CurrentLocals.Count > 0)
                {
                    var currentToday = CurrentLocals.FirstOrDefault(x => x.ThoiGian!.Contains(DateTime.Today.ToString("dd/MM/yyyy")))!;
                    if (currentToday != null)
                    {
                        SelectedCurrentLocal = currentToday;
                        CurrentToday = currentToday;
                    }
                    else
                    {
                        SelectedCurrentLocal = CurrentLocals.FirstOrDefault()!;
                        CurrentToday = CurrentLocals.FirstOrDefault()!;
                    }
                }

                // LẤY DỮ LIỆU TỪ SHEETS THỜI TIẾT
                await GetWeathersFromSheets();
                if (Weathers.Count > 0 && SelectedLocation != null)
                {
                    UpdateButtons();
                    UpdateButtonAgris();
                }
            }
            catch (Exception ex)
            {
                await Shell.Current.DisplayAlert("Thông báo", $"Có lỗi xảy ra khi làm mới dữ liệu: {ex.Message}", "OK");
            }
            finally
            {
                IsBusy = false;
                await _loadingService.HideLoadingAsync();
            }
        }
        [RelayCommand]
        private async Task ShowAlert(CurrentLocal value)
        {
            if (value != null)
            {
                string message = $"Dự báo thời tiết ngày: {value.ThoiGian}: {ConvertHelper.ConvertLocalWeather((int)value.ThoiTiet!)}, ";
                if (value.Tm != null && value.Tx != null) message += $"Nhiệt độ: {value.Tm} - {value.Tx} °C, ";
                else if (value.Tm != null) message += $"Nhiệt độ thấp nhất: {value.Tm} °C, ";
                else if (value.Tx != null) message += $"Nhiệt độ cao nhất: {value.Tx} °C, ";
                if (value.DoAm != null) message += $"Độ ẩm: {value.DoAm} %, ";
                if (value.R != null) message += $"Lượng mưa: {value.R} mm, ";
                if (value.XacSuatMua != null) message += $"Xác suất mưa: {value.XacSuatMua} %, ";
                if (value.TocDoGio != null) message += $"Tốc độ gió: {value.TocDoGio} m/s, ";
                if (value.HuongGio != null)
                {
                    message += $"Hướng gió: ";
                    foreach (var item in value.HuongGio)
                    {
                        if (item.ToString().Equals("N")) message += "Bắc ";
                        else if (item.ToString().Equals("E")) message += "Đông ";
                        else if (item.ToString().Equals("S")) message += "Nam ";
                        else if (item.ToString().Equals("W")) message += "Tây ";
                    }
                }
                await SpeakMessage(message);
            }
        }

        private async Task GetWeathersFromSheets()
        {
            try
            {
                Weathers.Clear();
                Weathers = await _googleSheetsService.ReadEntireSheetAsync(ConstantHelper.GoogleSpreadsheetId, "Thời tiết");
            }
            catch (Exception ex)
            {
                await _toastService.ShowAlert("Thông báo", $"Có lỗi xảy ra khi tải dữ liệu từ Google Sheets: {ex.Message}", "OK");
            }
        }
        private void OnButtonClicked(string subLocation)
        {
            string link = string.Empty;
            for (int i = 1; i < Weathers.Count; i++)
            {
                if (Weathers[i][4].Equals(subLocation))
                {
                    link = Weathers[i][7].ToString()!;
                    break;
                }
            }

            if (string.IsNullOrEmpty(link)) return;
            MsnWebUrl = link;
        }
        private void OnButtonAgriClicked(string s)
        {
            if (s.Equals("Nho"))
            {
                GuideTitle = "SÂU BỆNH HẠI CÂY NHO VÀ BIỆN PHÁP PHÒNG TRỪ";
                GuideDescription = "1. SÂU HẠI\r\n1.1. Sâu xanh da láng: (Spodoptera exigua). \r\n* Nhận dạng:\r\n- Thành trùng có kích thước trung bình, thân dài 18-20 mm, sải cánh rộng 30-35 mm, màu nâu xám nhạt, cuối bụng con cái có một chùm lông.\r\n- Trứng được đẻ tập trung vào nửa đêm thành từng ổ, mỗi ổ có hàng trăm trứng. Trên ổ trứng có phủ một lớp lông màu trắng hoặc vàng nhạt.\r\n- Sâu non có màu xanh nhạt, da bóng láng trên lưng có năm sọc, 2 sọc ở mỗi bên hông rất to và đậm, sọc giữa lưng có màu đen xen kẽ màu trắng. Nhộng màu nâu sẫm hay đỏ sẫm thường ở trong đất.\r\n* Tập quán sinh sống và cách gây hại:\r\n- Bướm đẻ trứng ở mặt dưới lá nho, sâu nở ra có màu xanh lá cây tập trung cắn phá quanh ổ. Khi lớn sâu phát tán cắn phá các phần non như đọt lá, chùm hoa, trái non,... Hậu quả làm hạn chế sinh trưởng, giảm năng suất cây trồng.\r\n- Khi nho già, sâu cắn phá làm tàn lụi lá, ảnh hưởng đến năng suất vụ sau. Sâu phát sinh trong điều kiện khô nóng. Tại Ninh Thuận sâu phát triển mạnh từ tháng 1 đến tháng 7. Đây là loại sâu hại rất khó phòng trừ bằng biện pháp hóa học.\r\n* Biện pháp xử lý:\r\n- Làm sạch cỏ ở dưới và chung quanh giàn Nho. \r\n- Dùng tay bắt và giết sâu, giết ổ trứng, ngắt bỏ các lá có sâu mới nở. \r\n- Khi sâu xuất hiện nhiều (sâu tuổi 1, 2 có mật độ trên 50 con/m2) thì ưu tiên dùng các loại thuốc sinh học.\r\n- Cần chú ý ở các vùng trồng hành tây cần phải tập trung gom bỏ hay thiêu hủy cọng lá hành có chứa trứng và sâu để tránh lây lan sang cây Nho.\r\n- Khi sâu xuất hiện nhiều (sâu tuổi 1, 2) thì sử dụng luân phiên một số loại thuốc như: Aremec 45EC, Luckyler 25EC, Bafurit 5WG, Tungmectin 5EC,… để phun phòng trừ.\r\n1.2. Nhện đỏ: (Tetranychus sp.).\r\n* Nhận dạng:\r\n- Thành trùng hình bầu dục, thân rất nhỏ khoảng 0,4 mm, thành trùng đực có kích thước nhỏ khoảng 0,3 mm. Toàn thân phủ lông lưa thưa và thường có màu đỏ với đốm đen ở 2 bên thân mình. Nhện có 8 chân, thành trùng cái màu vàng nhạt hay hơi ngả sang màu xanh lá cây. Nhìn xuyên qua cơ thể có thể thấy được hai đốm màu đậm bên trong, đó là nơi chứa thức ăn. Sau khi bắt cặp, thành trùng cái bắt đầu đẻ trứng từ 2-6 ngày, mỗi nhện cái đẻ khoảng 70 trứng.\r\n- Trứng rất nhỏ, hình cầu hoặc hình củ hành, bóng láng và được đẻ sát gân lá ở cả hai mặt lá (thường là được gắn chặt vào mặt dưới của lá, ở những nơi có tơ do nhện tạo ra trong khi di chuyển). Trứng nở sau khoảng 4-5 ngày.\r\n- Ấu trùng nhện đỏ rất giống thành trùng nhưng chỉ có 3 đôi chân. Những ấu trùng thay da 3 lần là con cái, những ấu trùng thay da 2 lần là thành trùng đực. Giai đoạn ấu trùng phát triển từ 5-10 ngày.\r\n- Nhện đỏ hoàn tất một vòng đời từ 20-40 ngày.\r\n* Tập quán sinh sống và cách gây hại:\r\n- Nhện di chuyển rất nhanh và nhả tơ mỏng bao thành một lớp ở mặt dưới lá nên trông lá có màu trắng, do lớp da để lại sau khi lột cùng với bụi và những tạp chất khác.\r\n- Chúng sinh sống và gây hại ở mặt dưới lá, cả ấu trùng và thành trùng nhện đỏ (nhện trưởng thành và nhện non) đều ăn biểu bì và chích hút mô dịch của lá cây khi lá bước vào giai đoạn bánh tẻ trở đi, làm cây bị mất màu xanh và có màu vàng, làm cho mặt trên của lá bị vàng loang lổ (những chấm trắng vàng rất rễ nhận ra trên mặt lá), còn ở mặt dưới lá có những vết trắng lấm tấm giống bụi cám, nhìn kỹ thấy trên đó có lớp tơ rất mỏng.\r\n- Khi nhện hại nặng lá cây bị phồng rộp sau đó cằn lại, vàng, thô cứng và sau cùng lá sẽ bị khô đi. Màu vàng của lá dễ nhìn thấy nhất là ở mặt dưới lá, làm giảm phẩm chất và năng suất trái. Khi mật số cao, cả cành non cũng bị nhện đỏ tấn công, cành cũng trở nên khô và chết.\r\n- Nhện đỏ gây hại làm cho trái bị vàng, sạm và nứt khi trái lớn. Hoa bị hại có thể bị thui, rụng. Nhện còn có thể tuyền bệnh virus cho cây.\r\n- Nhện đỏ phát triển trong điều kiện mùa nắng, khô hạn và cây bón nhiều đạm. Do có vòng đời ngắn nên thường mật số tăng lên rất nhanh và gây hại nghiêm trọng.\r\n- Chúng lan truyền nhờ gió và những sợi tơ, mạng của chúng. \r\n- Nhện đỏ chủ yếu phá hại trên lá già và lá bánh tẻ. Giàn Nho cuối vụ thường bị hại nặng, bộ lá có thể bị hư rụi toàn bộ, ảnh hưởng đến năng suất vụ sau. \r\n* Biện pháp xử lý: Dùng các loại thuốc sau để trừ: Azimex 40EC, Etimex 2.6EC, Dibonin 5WP, 5SL, Kobisuper 1SL, Sam spider 500WP,... để phun phòng trừ.\r\n1.3. Bọ trĩ: (Rhipiphorothrips cruentatus).\r\n* Nhận dạng:\r\nBọ trĩ non và trưởng thành cơ thể rất giống nhau, đều thon nhọn phía sau, dài khoảng 01 mm, màu vàng hoặc vàng nâu, tập trung ở mặt dưới của lá, di chuyển nhanh. Trứng đẻ trong phần non của lá hoặc đọt non.\r\n* Tập quán sinh sống và cách gây hại:\r\nCả trưởng thành và bọ trĩ non đều gây hại bằng cách cứa rách mô tế bào biểu bì và hút nhựa chảy ra, do đó sẽ khiến nho không xanh mướt mà có màu xỉn, lá nho phía dưới có màu ánh bạc còn phía trên lá cong mo lại. Nếu bọ trĩ gây hại ở chùm hoa thì hoa nhỏ, rụng do cuống hoa tổn thương làm tỷ lệ đậu trái thấp. Trường hợp bọ trĩ gây hại khi quả non thì trái nho trong chùm không đều, trên vỏ trái có các vết sần sùi làm xấu trái bán giá thấp. Bọ trĩ phát sinh và gây hại nặng trong điều kiện khô và nóng, triệu chứng cháy lá, rụng hoa càng xảy ra nhanh trong điều kiện trời nắng nóng.\r\n* Biện pháp xử lý: Có thể sử dụng các loại thuốc như: Radiant 60SC, Reasgant 3.6EC, Shertin 5.0EC, Angun 5 WG, Actimax 50WG, Agassi 55EC, DT Ema 40EC; Tasieu 1.9EC,... để phun phòng trừ.\r\n1.4. Rệp sáp: (Ferrisiana virgata).\r\n* Nhận dạng:\r\nRệp tiết ra một lớp sáp che chở cho cơ  thể, lớp này hình thành nên một lớp vỏ cứng, có hình dạng, màu sắc và kích thước khác nhau hoặc lớp phấn trắng. Lớp vỏ của Rệp sáp có thể tách ra khỏi cơ thể một cách dễ dàng.\r\n* Tập quán sinh sống và cách gây hại:\r\n- Rệp sáp có chu kỳ sinh trưởng ngắn (dưới 1 tháng trong điều kiện nhiệt độ không khí cao liên tục), khả năng sinh sản cao, nếu điều kiện môi trường thích hợp sẽ có khả năng bộc phát nhanh.\r\n- Cả ấu trùng và thành trùng cái chích hút lá, cành, trái, cuống trái. Nếu bị nhiễm nặng, lá bị vàng, rụng, cành bị khô và chết, trái cũng có thể bị biến màu, phát triển kém và bị rụng.\r\n- Rệp sáp gây hại chủ yếu vào mùa nắng. Mật ngọt do rệp tiết ra còn hấp dẫn nấm bồ hóng phát triển làm ảnh hưởng đến sự quang hợp của cây. Nguy hại hơn, rệp làm cho chồi Nho bị co cúm lại, giảm khả năng ra hoa và  giảm chất lượng quả. \r\n- Rệp phá hại hầu hết các bộ phận của cây, chúng bám vào cành ngọn non, lá và chùm quả để hút nhựa cây, hậu quả làm giảm sự sinh trưởng và phát triển của Nho. Thân rệp có phủ 0l lớp sáp như bông, vì vậy nông dân thường gọi là rầy bông.\r\n* Biện pháp xử lý:\r\n- Thường xuyên tỉa cành, tạo tán sẽ giúp hạn chế rệp sáp.\r\n- Thường xuyên kiểm tra vườn để phát hiện và phun thuốc kịp thời, đặc biệt là giai đoạn cây đang có đọt non, lá non, bông, trái non. Nên phun trực tiếp vào chỗ có rệp đeo bám.\r\n- Trước khi phun thuốc nên phun nước có pha xà bông để rửa trôi bớt lớp phấn sáp bên ngoài, để khi phun thuốc thì thuốc dễ tiếp xúc với cơ thể của rệp, hiệu quả diệt rệp của thuốc sẽ cao hơn. Cũng có thể dùng máy bơm nước có áp suất cao xịt mạnh tia nước vào chỗ có nhiều rệp để làm trôi bớt rệp.\r\n- Dùng các loại thuốc như Mativex 1.5EW... để phun phòng trừ.\r\n2. BỆNH HẠI\r\n2.1. Mốc sương: (Downy mildew).\r\n* Tác nhân gây bệnh: Bệnh do Plasmopara viticola gây ra. \r\n* Triệu chứng:\r\n- Nấm chủ yếu tấn công vào lá non và lá bánh tẻ.\r\n- Triệu chứng đầu tiên là xuất hiện các vết màu vàng với kích thước và hình dạng không đồng đều, sau đó mọc lên các bào tử nấm màu trắng.\r\n- Nấm còn tấn công cả vào hoa làm hoa bị tiêu hủy.\r\n- Những quả bị bệnh nấm mốc sương gây ra có màu vàng hơi đỏ, bị chín ép và rụng mà ít được người trồng nho nhận thấy..\r\n* Điều kiện phát sinh phát triển:\r\n- Bệnh xuất hiện vào thời kỳ nho sinh trưởng mạnh về thân lá, gây hại nặng trong điều kiện thời tiết ẩm, mưa nhiều (như các tháng 9, 10, 11) và một số thời điểm có sương nhiều của vụ khô.\r\n- Nấm có nguồn gốc từ lá bệnh trên cây và lá rụng, còn có trong mầm cây, nấm bệnh là dạng tồn tại qua mùa đông.\r\n- Nấm tấn công các phần xanh của  cây, nhất là ở chùm hoa, lá và trái non. Khi bị bệnh nặng cây bị hư 1 phần lá hay rụng lá làm giảm sinh trưởng và phát triển, giảm năng suất và phẩm chất trái về sau. Chùm bông, chùm trái bị bệnh hóa nâu, sau đó khô héo rụng cục bộ hay toàn phần, trái phát triển èo uột.\r\n* Biện pháp xử lý:\r\n- Tiêu thoát nước vườn Nho khi bị đọng nước do mưa nhiều và làm tốt công tác vệ sinh đồng ruộng.\r\n- Bón phân NPK đầy đủ và cân đối, tăng cường bón phân Kali và hạn chế sử dụng nhiều phân đạm hay phân bón lá vào các vụ có áp lực bệnh cao.\r\n- Chỉ nên phun thuốc khi cần thiết, có thể dùng các loại thuốc như Alimet 80WP, Melody duo 66.75WP, MAP Green 6SL,… khi thấy điều kiện thời tiết thích hợp cho bệnh xuất hiện, nếu bệnh nặng có thể dùng Melody duo 66.75WP, Danjiri 10SC,… để phun phòng trừ.\r\n2.2. Bệnh Phấn trắng: (Powdery mildew).\r\n* Tác nhân gây bệnh: Do nấm Uncinula necator gây hại.\r\n* Triệu chứng:\r\n- Nấm bệnh tấn công các phần xanh của cây như cành xanh, lá già, lá bánh tẻ và trái. Cả 2 mặt lá khi bị  nhiễm bệnh trở nên mất màu, có vết đốm sáng như giọt dầu, nhất là ở mặt trên lá, lá non bị biến dạng còi cọc.\r\n- Trên quả có thể thấy rõ các vết màu trắng hơi xám của bào tử nấm, khi chùi lớp bào tử bên ngoài thì sẽ nhìn rõ vết bệnh màu trắng phía trong, cuống trái, chùm trái dòn dễ gãy.\r\n- Bệnh nguy hiểm cho trái từ lúc quả còn non đến khi quả chín, làm biểu bì trái bị chết, khiến trái nứt về sau và dể thối khi chín do nấm Botrytis cinerea gây ra, những trái bệnh nhưng không bị nứt, khi chín sẽ mất màu hay màu không được sáng, hương vị và phẩm chất kém. \r\n- Trên cành và lá thấy xuất hiện các đốm mốc màu xám tro, trên quả có thể thấy rõ các vết màu trắng hơi xám của bào tử nấm, khi chùi lớp bào tử bên ngoài đi lộ rõ vết bệnh màu xám tro ở phía trong.\r\n- Bệnh đặc biệt nghiêm trọng cho giai đoạn quả từ khi đậu được 5-7 ngày cho tới khi chín.\r\n* Điều kiện phát sinh phát triển:\r\n- Nhiều nhất vào vụ Đông xuân có gió mùa Đông Bắc thổi mạnh, trời âm u, ánh sáng yếu và khuếch tán, ẩm độ 40-90%, nhiệt độ từ 20-270C là điều kiện thích hợp cho nấm phát triển.\r\n- Trong điều kiện ở Ninh Thuận, nấm phát triển hầu như quanh năm, trừ các tháng mưa lớn. Những giàn nho thiếu ánh sáng thuận lợi cho sự phát sinh và lây lan của bệnh.\r\n* Biện pháp xử lý:\r\n- Duy trì mật độ cành hợp lý 8-10 cành/m2 để ruộng Nho có đầy đủ ánh sáng và  thông thoáng.\r\n- Gom sạch và đốt cành lá bệnh sau khi cắt cành để phòng ngừa bào tử nấm lây lan.\r\n- Bón phân NPK đầy đủ và cân đối, hạn chế dùng nhiều phân  đạm.\r\n- Khi bị nặng thì dùng các loại thuốc như  lưu huỳnh vôi, các chế phẩm có chứa đồng, thuốc Bosstar 25SC, Lime Sulfur, Insuran 50WG, ... để phun phòng trừ.\r\n2.3. Bệnh nấm cuống:\r\n* Tác nhân gây bệnh: Bệnh do nấm Diplodia gây ra.\r\n* Triệu chứng:\r\n- Bệnh chủ yếu gây hại trên cuống trái, từ khi bắt đầu ra hoa đến khi trái lớn và chín. Bệnh hầu như không gây hại trên lá và trái như các bệnh sương mai, phấn trắng.\r\n- Quan sát trên cuống chùm hoa hoặc cuống chùm trái sẽ thấy những vết màu nâu, lúc đầu hơi ướt, về sau làm khô teo một đoạn cuống, gây tắc mạch dẫn nước và dinh dưỡng.\r\n- Tùy theo vết bệnh xuất hiện ở cuống lớn hay cuống nhánh, cuống nhỏ mà làm héo từng phần hay toàn bộ chùm hoa, chùm trái, làm giảm năng suất đáng kể.\r\n- Ngoài ra, nấm bệnh còn tiếp tục phá hại cuống trái nho trong quá trình bảo quản và vận chuyển làm rụng và thối trái \r\n* Điều kiện phát sinh phát triển:\r\nBệnh thường phát sinh vào những tháng mưa nhiều, thời tiết nóng ẩm, có nhiều sương hay lúc trời âm u. Nấm tấn công vào cây từ khi hoa mới ra đến khi sắp thu hoạch. \r\n* Biện pháp xử lý:\r\n- Sử dụng các loại giống sạch bệnh để đưa vào sản xuất.\r\n- Đảm bảo mật độ trồng, khoảng cách trồng để đảm bảo độ thông thoáng.\r\n- Bón phân, tỉa cành tạo tán và vệ sinh vườn thông thoáng để hạn chế nguồn nấm bệnh trên thực vật tồn dư có thể lây nhiễm trở lại trên cây. \r\n- Khi cây bắt đầu nhú hoa cho đến khi trái lớn nên phun thuốc phòng trừ  nấm 2-3 lần/vụ. Sử dụng luân phiên các loại thuốc bảo vệ thực vật theo hướng dẫn trên bao bì.\r\n2.4. Bệnh rỉ sắt: \r\n* Tác nhân gây bệnh: Bệnh do nấm Uromyces vignae gây ra.\r\n* Triệu chứng:\r\nNấm chủ yếu gây hại trên lá bánh tẻ và lá già, nấm bệnh màu vàng rỉ sắt.\r\n* Điều kiện phát sinh phát triển:\r\nThường thấy nấm xuất hiện vào cuối vụ trong các tháng mưa nhiều, nấm có thể làm tàn lụi bộ lá trước khi cắt cành, việc giảm diện tích quang hợp sẽ ảnh hưởng đến năng suất vụ sau. \r\n* Biện pháp xử lý: Dùng các loại thuốc như Vivil 5SC,... để phun phòng trừ.\r\n2.5. Bệnh thán thư: (Antharacnose-black spot).\r\n* Tác nhân gây bệnh: Gồm nhiều loại khác nhau: Elsinoe ampelina (De Bary), Sphaceloma ampelinum (De Bary), Colletotrichum glocosporioides (Penz) gây ra.\r\n* Triệu chứng:\r\n- Trên lá: những lá non dễ nhiễm bệnh, đầu tiên là những đốm nhỏ màu nâu đậm, ở giữa màu xám nhạt, sau bị thủng. Khi bị nặng làm lá biến dạng.\r\n- Trên chồi: Chồi non chồi mộng nước rất dễ nhiễm bệnh. Những vết bệnh nhỏ màu nâu sẽ lớn dần và phát triển thành oval màu tía, lõm xuống với những mép viền màu đen.\r\n- Trên trái: Xuất hiện những vết nhỏ màu đen, thường ở giữa màu xám. Khi cuống trái bị nấm tấn công thì trái không thể phát triển được.\r\n* Điều kiện phát sinh phát triển: Bệnh phát triển mạnh trong mùa mưa và khí hậu ẩm. \r\n* Biện pháp xử lý:\r\n- Cần chú trọng các biện pháp tổng hợp như vệ sinh đồng ruộng, dọn sạch tàn dư bị bệnh sau một mùa vụ nhất là các cành cây khô đã bị nhiễm bệnh, tránh bón quá nhiều đạm về cuối vụ, thoát nước vào mùa mưa thật tốt, mật độ cành hợp lý và tăng cường bón phân kali ở giai đoạn cuối để tăng đề kháng cho cây Nho.\r\n- Phối hợp sử dụng thuốc BVTV với việc bao chùm trái Nho vào mùa mưa. Có thể dùng các loại thuốc như Genol 1.2SL, Kamsu 2SL, 8WP, Actinovate 1 SP, Actino-Iron 1.3 SP,… để phun phòng trừ.";
            }
            else if (s.Equals("Lúa"))
            {
                GuideTitle = "SÂU BỆNH HẠI CÂY LÚA VÀ BIỆN PHÁP PHÒNG TRỪ";
                GuideDescription = "1. SÂU HẠI\r\n1.1. Rầy nâu: (Nilaparvata lugens).\r\n* Nhận dạng:\r\n- Rầy nâu trưởng thành màu nâu, dài 3-5 mm, cánh trong suốt. Rầy cái to hơn rầy đực.\r\n- Rầy trưởng thành có 2 dạng: Dạng cánh dài che hết bụng và dạng cánh ngắn không che hết bụng.\r\n* Tập quán sinh sống và cách gây hại:\r\n- Rầy nâu sống tập trung ở gốc lúa, bám quanh bẹ lúa nơi gần mặt nước, mật số cao có thể bám lên lá. Rầy trưởng thành dạng cánh ngắn không bay được, dạng cánh dài có thể bay rất xa và thích vào đèn ban đêm.\r\n- Rầy nâu còn là tác nhân lây truyền bệnh lúa cỏ, vàng lùn (lúa cỏ dòng 2), lùn xoắn lá là những bệnh rất nguy hiểm đối với cây lúa.\r\n* Biện pháp xử lý:\r\n- Dùng giống kháng rầy.\r\n- Không sạ dày (> 200 kg/ha); bón cân đối đạm, lân, kali, không bón phân đạm nhiều và bón muộn.\r\n- Hạn chế dùng thuốc trừ sâu để bảo vệ thiên địch của rầy như bọ xít nước, bọ xít mù xanh, bọ rùa, nhện,... Nếu mật độ rầy cao thì diệt rầy bằng cách: Dùng dầu nhớt rải xuống nước, quậy cho dầu loang đều xong kéo, khua cho rầy rơi xuống nước hoặc tát nước lên gốc lúa (áp dụng diện hẹp).\r\n- Phun Nấm xanh (Metarhizium anisoplae) để hạn chế mật số rầy gia tăng; khi thấy rầy trưởng thành nhiều, tháo cạn nưóc từ 3-4 ngày cho rầy đẻ trứng ở bẹ lá lúa sau đó cho nước vào để làm thối trứng.\r\n- Dùng các loại thuốc như Cruiser® 350FS, Admitox 600SC để xử lý hạt giống trước khi gieo sạ. Khi xuất hiện mật số rầy từ 1.000-2.000 con/m2, Lúa ở giai đoạn trước trổ và >2.000 con/m2 ở giai đoạn sau trổ, dùng các loại thuốc có hiệu quả trừ rầy nâu cao như Mospilan 20SP, Pexena® 106SC, Applaud 25SC, Chess® 50WG, Bassa 50EC,… để phòng trừ. Khi phun hướng vòi phun vào sát gốc Lúa nơi rầy tập trung gây hại; trước khi phun thuốc cho nước vào ruộng để rầy di chuyển lên cao, phun dễ trúng rầy hơn.\r\nChú ý: Khi có dịch bệnh vàng lùn - lùn xoắn lá thì phun trừ triệt để rầy cánh dài di trú khi lúa dưới 20 ngày tuổi.\r\n1.2. Sâu đục thân lúa 2 chấm: (Schoenobius incertulas).\r\n* Nhận dạng:\r\n- Sâu đục thân có nhiều loài, mỗi loài có hình dạng, màu sắc của trứng, sâu, nhộng, bướm khác nhau, phổ biến nhất là sâu đục thân bướm 2 chấm.\r\n- Bướm sâu đục thân 2 chấm có cánh màu vàng nhạt, trên mỗi cánh trước có 1 chấm đen ở giữa.\r\n- Ổ trứng hình nửa hạt đậu thường được đẻ trên lá lúa có một lớp lông tơ che phủ. Mỗi ổ trứng có 30-100 trứng.\r\n- Sâu non tuổi nhỏ màu trắng sữa, lớn màu vàng nhạt sống trong thân lúa.\r\n- Nhộng màu vàng trong thân gần gốc lúa.\r\n* Tập quán sinh sống và cách gây hại:\r\n- Bướm ban ngày ẩn nấp trong khóm lúa, lùm cỏ, hoạt động giao phối và đẻ trứng ban đêm, thích ánh sáng đèn.\r\n- Sâu non mới nở di chuyển xuống dưới đục qua bẹ vào thân lúa rồi xuống gốc cây lúa. Bị hại ở giai đoạn đẻ nhánh, cây lúa dễ bị gãy, đọt lúa bị héo khô có thể rút ra dễ dàng. Nếu lúa đã trổ, sâu cắn đứt thân làm cho bông lúa bị héo khô, bạc trắng gọi là bông bạc.\r\n- Vòng đời 45-60 ngày, thời gian sâu non 30-40 ngày.\r\n* Biện pháp xử lý:\r\n- Sau mỗi vụ nên cày vùi gốc rạ để diệt nhộng.\r\n- Ngắt bỏ ổ trứng.\r\n- Bón phân cân đối tránh dư đạm.\r\n- Thường cây lúa có khả năng bù đắp lớn khi bị sâu đục thân gây hại bằng cách: Giai đoạn đẻ nhánh thì ra nhánh mới, ở giai đoạn đang trổ khi một số chồi bị hại thì dinh dưỡng sẽ tập trung vào những chồi còn lại.\r\n- Nếu phát hiện bướm sâu đục thân hoặc mật độ sâu trên đồng ruộng đến ngưỡng phòng trừ (giai đoạn mạ - đẻ nhánh: 5-10% dảnh héo hoặc 0,25-0,5 ổ trứng/m2; giai đoạn đòng - trổ: 2,5-5% bông bạc hoặc 0,15-0,3 ổ/m2), sử dụng các loại thuốc hạt để rải như: Vibam 5GR, Gà nòi 4GR, Prevathon® 0,4GR,… và thuốc Virtako® 40WG, Padan 95SP, Gà nòi 95SP, Prevathon® 5SC,… để phun phòng trừ. Đối với những diện tích có mật độ sâu cao cần phải phun kép 2 lần, lần 2 cách lần 1 khoảng 3-5 ngày để đạt hiệu quả cao.\r\n1.3. Sâu cuốn lá nhỏ: (Cnaphalocrosis medinalis).\r\n* Nhận dạng:\r\n- Bướm màu vàng nâu, mỗi cánh trước có 2 đường vằn ngang.\r\n- Trứng màu trắng trong dạng bầu dục, đẻ rải rác trên mặt lá gần gân chính.\r\n- Sâu màu xanh lá mạ ửng vàng nhạt, khi động đến thì búng mạnh và nhả tơ.\r\n- Nhộng màu nâu sậm.\r\n* Tập quán sinh sống và cách gây hại:\r\n- Bướm thích đẻ trứng ở nơi lá xanh đậm, nơi bóng mát. Sâu non nhả tơ cuốn lá lúa lại sống bên trong gặm ăn chất xanh để lại biểu bì trắng, sau đó chuyển sang lá khác tiếp tục gây hại.\r\n- Vòng đời 30-35 ngày, thời gian sâu non 20-25 ngày.\r\n* Biện pháp xử lý:\r\n- Vụ Đông Xuân sâu cuốn lá thường phát sinh mạnh nên sạ mật độ vừa phải, giảm phân đạm.\r\n- Hạn chế dùng thuốc trừ sâu để bảo vệ thiên địch (rất nhiều loài). Trồng những cây có hoa trên bờ ruộng để thu hút thiên địch như sao nháy, xuyến chi,...\r\n- Cây lúa có khả năng bù đắp rất lớn vì với khoảng <50% diện tích lá bị hại ở giai đoạn đẻ nhánh không làm giảm hoặc giảm rất ít năng suất nếu cây được chăm sóc tốt sau đó. Khi mật độ sâu gây hại ở giai đoạn lúa đẻ nhánh: 25-50 con/m2 và giai đoạn đòng, trổ: 10-20 con/m2, sử dụng các loại thuốc hóa học như: Virtako® 40WG, Padan 95SP, SecSaigon 25EC, Prevathon® 5SC,… để phun phòng trừ.\r\n1.4. Sâu phao: (Nymphula depunctalis).\r\n* Nhận dạng:\r\n- Ngài nhỏ, mỏng manh, màu trắng tuyết với những đốm vàng nâu nhạt ở cả 2 cánh.\r\n- Trứng tròn, vàng nhạt, đẻ thành 1-2 hàng ở bẹ lá hoặc mặt dưới lá gần mặt nước.\r\n- Sâu non xanh trong, đầu vàng nâu, có 5 tuổi, dài khoảng 20 mm khi đẫy sức.\r\n- Nhộng làm tổ ở những ống lá màu nâu ở gần gốc lúa.\r\n* Tập quán sinh sống và cách gây hại:\r\n- Ngài hoạt động vào ban đêm, ưa mùi chua ngọt, thích ánh sáng yếu.\r\n- Sâu non tuổi 1-2 gặm bề mặt lá, rồi ăn khuyết từng miếng nhỏ, từ tuổi 3 trở đi có thể cắn đứt hẳn lá, dảnh mạ, lúa. Sâu thường ăn vào ban đêm, đối với những ngày trời râm mát, mưa phùn sâu có thể phá cả ngày.\r\n- Sâu làm nhộng ở các khe nứt nẻ, vùng đất xung quanh gốc lúa.\r\n- Sâu thường phá thời kỳ mạ, lúa đẻ nhánh, sâu thường phá rất nhanh có thể cắn trụi ruộng này sang ruộng khác, những năm mưa nhiều ngập úng sâu thường gây hại nặng.\r\n* Biện pháp xử lý:\r\n- Biện pháp kỹ thuật:\r\n+ Cho nước vào ngập ruộng dùng rổ vớt hết các phao sâu;\r\n+ Thoát nước nhiều ngày có thể diệt được sâu phao nhưng cỏ dại mọc nhiều.\r\n- Xử lý thuốc khi thật cần thiết, dùng các loại thuốc Anb40 Super 1.8EC, 22.2WP, Season 450SC, Tungrin 25EC, Proclaim® 1.9 EC,… để phun phòng trừ.\r\n1.5. Bọ trĩ (bù lạch): (Baliothrips biformis, Thrips oryzae).\r\n* Nhận dạng:\r\n- Bọ trĩ rất nhỏ, dài 1-2 mm. Con trưởng thành màu nâu hoặc đen, ấu trùng màu trắng sữa hoặc vàng nhạt.\r\n-  Trứng rất nhỏ đẻ từng quả trên lá non khó thấy.\r\n-  Muốn phát hiện thấm ướt bàn tay rồi gạt ngang trên ngọn lúa, nếu có bọ trĩ sẽ dính vào tay.\r\n* Tập quán sinh sống và cách gây hại:\r\n- Chỉ xuất hiện ở giai đoạn mạ và lúa còn nhỏ, gây hại nặng khi nắng hạn, khô nước. Xuất hiện nhiều trong vụ Hè Thu, nhất là thời điểm hạn hán.\r\n- Trưởng thành và ấu trùng sống tập trung ở đầu lá lúa chích hút nhựa làm đầu lá lúa cuốn lại cháy khô có màu vàng đỏ.\r\n- Vòng đời 15-20 ngày, trưởng thành có thể sống 2-3 tuần.\r\n* Biện pháp xử lý:\r\n- Bọ trĩ chỉ gây hại ở giai đoạn lúa non là thời kỳ cây lúa có khả năng phục hồi rất mạnh nếu được bón phân sau đó, do vậy không hoặc ít ảnh hưởng đến năng suất.\r\n- Khi bọ trĩ xuất hiện nhiều nếu chủ động nước thì lấy nước vào ruộng ngập lúa 24 giờ sau đó rút nước ra.\r\n- Phun các loại thuốc như: Abakill 3.6EC, Alibaba 6.0EC, Anphatox 100SC, Fastac 5EC, Fortox 50EC, Cypermap 25EC, Sutin 5EC,…\r\n1.6. Bọ xít hôi (bọ xít dài): (Leptocorisa varicornis, L.acuta).\r\n* Nhận dạng:\r\n- Bọ xít trưởng thành dài 15-20 mm, màu nâu vàng, chân dài, râu dài.\r\n- Trứng hình bầu dục, màu nâu đen đẻ thành hàng dọc theo lá lúa, bẹ lúa hoặc bông lúa.\r\n- Bọ xít non nhỏ hơn trưởng thành, màu xanh lá mạ không có cánh.\r\n- Không có giai đoạn nhộng.\r\n* Tập quán sinh sống và cách gây hại:\r\n- Bọ xít sợ nắng nên hoạt động mạnh lúc sáng sớm hoặc chiều mát, khi có động con trưởng thành bay nhanh. Bọ xít non và trưởng thành có mùi hôi.\r\n- Bọ xít hôi xuất hiện và phá hại vào giai đoạn lúa ngậm sữa và trổ. Trước và sau đó bọ xít sống trên cỏ dại, bờ bụi. Trà lúa sớm và lúa muộn thường bị bọ xít gây hại, khi lúa trổ đại trà thì bọ xít phân tán nên mức hại ít hơn. Bọ xít hại lúa bằng cách dùng vòi chích qua nơi tiếp giáp của 2 vỏ trấu để hút chất sữa bên trong làm hạt lép hoặc lửng. Hạt bị bọ xít chích hút nếu không lép thì hạt gạo cũng dễ bể, phẩm chất giảm.\r\n- Vòng đời 25-30 ngày, bọ trưởng thành có thể sống hàng tháng.\r\n* Biện pháp xử lý:\r\n- Diệt lúa chét và cỏ dại vì đó là những cây ký chủ giúp bọ xít sống qua thời gian không có cây lúa trên đồng ruộng.\r\n- Nên gieo sạ đồng loạt, dùng những giống lúa có thời gian sinh trưởng tương đương nhau để khi lúa trổ đồng loạt sẽ giảm được tác hại.\r\n- Dùng bẫy bả, rỉ đường, cá ươn trộn thuốc trừ sâu để diệt bọ xít.\r\n- Thiên địch của bọ xít có ong ký sinh trứng, nhện ăn bọ non, nấm gây bệnh.\r\n- Khi mật độ bọ xít cao dùng các loại thuốc như: Tungatin 3.6EC, Bai 58 40EC, Cyrux 25EC, Cypermap 25EC, Sakumec 0.5EC,… phun vào sáng sớm hoặc xế chiều.\r\n1.7. Bọ xít đen: (Scotinophora lurida).\r\n* Nhận dạng:\r\n- Bọ trưởng thành hình gần như lục giác dài 7-8 mm, màu đen hoặc nâu đen.\r\n- Trứng đẻ thành ổ từ 10-15 trứng xếp thành những hàng dọc theo gân lá lúa phía gần mặt nước.\r\n- Bọ non hình dạng giống trưởng thành, không cánh màu nâu vàng,    không có giai đoạn nhộng.\r\n* Tập quán sinh sống và cách gây hại:\r\n- Bọ trưởng thành và bọ non sống tụ tập ở gốc lúa ban ngày, ban đêm di chuyển lên phía trên cây, trưởng thành vào đèn nhiều. Trong mùa khô bọ thường trú ẩn trong kẽ nứt của đất nơi có cỏ, khi thời tiết thích hợp sẽ di chuyển đến ruộng lúa sinh sản và gây hại.\r\n- Bọ xít đen có thể gây hại các vụ lúa khác nhau, tuy vậy mật độ và tác hại thường cao trong vụ Hè Thu, tập trung giai đoạn lúa đẻ nhánh rộ đến có đòng.\r\n- Vòng đời 50-60 ngày.\r\n* Biện pháp xử lý:\r\n- Làm sạch cỏ dại trong ruộng và bờ.\r\n- Không gieo sạ dày, bón phân cân đối để ruộng lúa thông thoáng không thuận lợi cho bọ xít phát sinh gây hại.\r\n- Thiên địch của bọ xít đen có ong ký sinh trứng, bọ cánh cứng, bọ ngựa ăn trứng, nấm gây bệnh bọ non.\r\n- Khi mật độ cao, dùng các loại thuốc như phòng trừ bọ xít hôi, phun trực tiếp vào gốc lúa.\r\n1.8. Nhện gié: (Steneostarsonemus spinki Smiley).\r\n* Nhận dạng:\r\n- Nhện gié có kích thước rất nhỏ, trên ruộng có thể lầm với những hạt phấn của hoa lúa. Quan sát kỹ thấy nhện màu đỏ có 4 cặp chân và tạo được lớp mạng bằng tơ rất mỏng.\r\n- Trứng rất nhỏ, màu trắng đục, đẻ rải rác trong một quần thể nhện phía trong bẹ lá.\r\n- Nhện non cơ thể nhọn dài và chỉ có 3 cặp chân.\r\n* Tập quán sinh sống và cách gây hại:\r\n- Nhện sống tập trung phía trên mặt nước, khi mật độ cao chúng mới bò lên bông lúa. Miệng nhện giống như vòi kim nhỏ chích hút nhựa ở bẹ lá, cuống bông, cuống gié và vỏ hoa lúa trước khi trổ làm bẹ lá có màu nâu thẫm, bông lúa trổ ra có nhiều hạt lép hoặc lép cả bông.\r\n- Một nhện trưởng thành cái đẻ khoảng 50 trứng, những trứng không thụ tinh trở thành con đực. Nhện non nở ra được nhện trưởng thành đực mang theo. Nhện non đẫy sức khoảng 1 ngày thì trở thành nhện trưởng thành. Vòng đời 10-12 ngày.\r\n* Biện pháp xử lý:\r\n- Cày lật gốc rạ sớm, diệt lúa chét giữa các vụ để hạn chế nguồn nhện lây lan.\r\n- Phun thuốc khi phát hiện có một số dảnh mới có triệu chứng bị hại (bẹ lá lúa bị hại có màu đỏ bã trầu). Dùng thuốc đặc trị nhện: Danitol-S 50EC, Kumulus 80WG, Comite® 73EC,… để phòng trừ.\r\n2. BỆNH HẠI\r\n2.1. Bệnh đạo ôn: (bệnh cháy lá, khô cổ bông, cổ gié).\r\n* Tác nhân: Do nấm Pyricularia oryzae gây hại.\r\n* Triệu chứng:\r\n- Trên lá xuất hiện nhiều đốm bệnh nhỏ bằng đầu kim sau lớn dần có dạng hình thoi màu nâu, giữa có màu xám trắng, xung quanh có viền vàng nâu. Trường hợp cấp tính, vết bệnh sẽ kéo dài ra theo chiều dọc lá, các vết bệnh lớn dần liên kết với nhau  làm lá khô hoàn toàn.\r\n- Trên thân bệnh gây hại ở đốt tạo thành những vết màu nâu bao quanh đốt thân làm đốt khô teo lại, cây lúa dễ bị gẫy gục.\r\n- Trên cổ bông, cổ gié vết bệnh màu nâu làm cả bông lúa hoặc gié lúa bị lép trắng hoặc lửng hạt. Vết bệnh cũng có thể xâm nhập lên vỏ hạt lúa.\r\n* Điều kiện phát sinh phát triển:\r\nBệnh có thể gây hại tất cả các giai đoạn từ mạ đến vào chắc - chín của cây lúa. Bệnh thường phát triển trong điều kiện: Giống nhiễm, bón phân đạm nhiều, ruộng khô, trời âm u, lạnh có sưong mù.\r\n* Biện pháp xử lý:\r\n- Dùng giống kháng bệnh đạo ôn là biện pháp hiệu quả nhất.\r\n- Sạ thưa hợp lý.\r\n- Bón cân đối đạm, lân, kali, ngưng bón phân đạm, không phun phân bón lá hoặc chất kích thích sinh trưởng khi bệnh chớm phát và không để ruộng bị khô.\r\n- Phun thuốc kịp thời khi bệnh mới phát sinh gây hại, dùng thuốc Nativo 750WG, Filia® 525SE, Kasai 21.2WP,… để phòng trừ.\r\n2.2. Bệnh khô vằn: (bệnh đốm vằn).\r\n* Tác nhân: Do nấm Rhizotonia solani gây hại.\r\n* Triệu chứng:\r\n- Bệnh xuất hiện đầu tiên ở bẹ lá, rồi từ đó lan dần lên phiến lá và cả bông lúa. Trên bẹ lá, vết bệnh lúc đầu tròn hay bầu dục, màu xám có viền nâu, sau lan ra không đều thành những vết loang lổ vằn vện như da hổ, bẹ lá khô tóp lại làm lá bị chết khô, bông lúa trổ bị nghẹn hoặc trổ nhưng hạt bị lép nhiều. Trên ruộng, bệnh xuất hiện thành từng ổ, sau đó có thể lan ra cả ruộng, làm ảnh hưởng đến năng suất. Triệu chứng rõ nhất từ thời kỳ lúa làm đòng đến lúc chín.\r\n- Trong điều kiện ẩm độ cao, trên các vết bệnh có lớp tơ nấm màu trắng, đôi khi có hạch nấm tròn. Bệnh làm ruộng lúa héo khô từng chòm, bông lúa bị lép, lửng.\r\n* Điều kiện phát sinh phát triển:\r\n- Bệnh khô vằn do nấm gây ra và phát triển tốt ở ẩm độ không khí cao (95-97%), nhiệt độ thích hợp 25-320C.\r\n- Bệnh gây hại ở tất cả các vụ trong năm, bệnh thường phát sinh trong điều kiện sạ dày, bón quá nhiều đạm và gây hại nặng từ giai đoạn lúa có đòng trở đi.\r\n* Biện pháp xử lý:\r\n- Cày lật đất sớm, bón vôi, ngâm nước một thời gian để diệt nguồn bệnh.\r\n- Gieo sạ mật độ vừa phải. Bón cân đối NPK, chú ý ruộng nào vụ trước đã có bệnh khi bón phân tập trung bón giai đoạn đầu không bón thúc muộn.\r\n- Khi ruộng lúa bị bệnh cần giữ mực nước ổn định không bón phân đạm.\r\n- Bệnh gây hại nặng vào giai đoạn lúa làm đòng - vào chắc, có thể sử dụng thuốc hóa học phun khi bệnh mới xuất hiện, nếu bệnh nặng phải tiến hành phun kép 2 lần, mỗi lần cách nhau 5-7 ngày. Các loại thuốc như: Curegold 375SC, Validacin 5SL, Anvil® 5SC, Amistar Top® 325SC, Sagograin 300EC, Ensino 400SC,… để phun phòng trừ.\r\n2.3. Bệnh vàng lá: (bệnh vàng lá chín sớm).\r\n* Tác nhân: Do nấm Gonatophragmium sp. gây hại.\r\n* Triệu chứng:\r\n- Vết bệnh lúc đầu là một đốm màu xanh đậm dạng giọt dầu sau lớn dần có màu vàng cam và kéo dài lên chóp lá. Các sọc này phát triển dần, làm cho lá lúa có màu vàng cam rồi cháy khô đi.\r\n- Đặc điểm để phân biệt bệnh \"vàng lá” với các bệnh khác là bên dưới vết vàng luôn có đốm hình bầu dục màu cam sậm (sậm hơn màu vàng của vết bệnh bên trên) hoặc xám trắng. Đó là vết tích của vết xâm nhiễm ban đầu và từ đốm này vết bệnh luôn lan lên phía chóp lá lúa chứ không lan xuống phía dưới.\r\n- Bệnh nặng và sớm làm bông lúa bị lép lửng nhiều.\r\n* Điều kiện phát sinh phát triển:\r\nBệnh thường xuất hiện từ giai đoạn lúa có đòng trở đi, bệnh phát triển mạnh trên giống nhiễm, sạ dày bón nhiều phân đạm, nhất là bón đón đòng và nuôi đòng quá nhiều và bón muộn.\r\n* Biện pháp xử lý:\r\n-  Dùng giống ít nhiễm bệnh. Sạ mật độ vừa phải.\r\n- Bón phân cân đối Đạm, Lân, Kali. Không bón đón đòng và nuôi đòng quá nhiều và bón muộn.\r\n- Phun thuốc khi bệnh xuất hiện nhiều, dùng thuốc Mix Perfect 525SC, Niko 72WP, Center super 333EC, Vimonyl 72WP,... để phun phòng trừ.\r\n2.4. Bệnh cháy bìa lá: (bệnh bạc lá).\r\n* Tác nhân: Do vi khuẩn Xanthomonas oryzae. gây hại.\r\n* Triệu chứng:\r\n- Bệnh xuất hiện ở mép lá, cháy dọc mép lá từ đầu chóp lá cháy xuống.\r\n- Bệnh lan theo chiều gió.\r\n- Những giọt keo vi khuẩn khô đọng lại ở mép lá màu vàng, nhỏ.\r\n- Đêm sương: Giọt keo vi khuẩn này tan ra, chảy chạy dài theo mép lá, và gió làm xây xát lan sang những lá khác.\r\n- Bệnh nặng: Lá lúa cháy đặc biệt lá đòng cháy làm lúa lép lửng cao, giảm năng suất nghiêm trọng.\r\n* Điều kiện phát sinh phát triển:\r\n- Bệnh thường xuất hiện từ giai đoạn lúa đứng cái trở về sau. Bệnh làm giảm  năng suất đáng kể nếu bị nặng trong giai đoạn làm đòng.\r\n- Bệnh phát triển mạnh ở những vùng có khí hậu nóng, ẩm, mưa gió nhiều, những ruộng có nước ngập tù đọng sạ dày, bón nhiều phân đạm.\r\n* Biện pháp xử lý:\r\n- Dùng giống lúa chống bệnh. Không gieo sạ dày.\r\n- Bón phân cân đối. Khi ruộng lúa có bệnh ngưng bón đạm, bón tăng cường phân Kali.\r\n- Phun thuốc Cadatil 33.5SC, Goldfull 500WP, Jonde 3SL, Hope Life 450WP, New Kasuran 16.6WP,… để phun phòng trừ, phải phun nhiều lần nhất là trong mùa mưa. Nhìn chung, khi vi khuẩn gây bệnh đã xâm nhập vào lá lúa rồi thì rất khó trị và tốn kém (phải phun nhiều lần). Do đó nếu bệnh thường xảy ra thì biện pháp phòng bệnh là hiệu quả nhất.\r\n2.5. Bệnh vàng lùn:\r\n* Tác nhân: Do virus Rice Grassy Stunt Virus (RGSV) gây ra. Virus này xâm nhập vào cây lúa và gây bệnh thông qua môi giới là Rầy nâu (Nilaparvata lugens).\r\n* Triệu chứng:\r\n- Lá lúa từ màu xanh nhạt chuyển dần sang màu vàng nhạt, vàng da cam rồi vàng khô. Vị trí các lá bị vàng lan dần từ các lá bên dưới lên các lá phía trên. Vết vàng trên lá xuất hiện từ chóp lá, lan dần vào phía bẹ lá. Tất cả các lá bị bệnh có xu hướng xòe ngang. Các chồi lúa bị bệnh giảm chiều cao và bệnh cũng làm giảm số chồi trên bụi lúa mắc bệnh. Quần thể ruộng lúa bị bệnh ngả màu vàng, chiều cao cây lúa không đồng đều.\r\n- Cây bị bệnh lùn hơn cây bình thường, mức độ lùn tùy thuộc thời gian nhiễm bệnh sớm (lùn nhiều) hay muộn (lùn ít). Lá lúa ngả màu vàng cam: trên lá vàng từ chóp vào, trên 1 chồi lúa lá bên dưới vàng trước lan dần lên lá bên trên. Trong một bụi lúa có thể chỉ một vài chồi mắc bệnh. Bệnh nặng làm cho chồi lúa hoặc cả bụi lúa chết rụi.\r\n- Virus gây bệnh không lây truyền qua hạt giống, nước, không khí, đất, tác nhân cơ giới mà chỉ được lây truyền qua môi giới là rầy nâu.\r\n* Điều kiện phát sinh phát triển:\r\n- Rầy nâu là môi giới truyền virus gây bệnh cho cây lúa và truyền virus cho đến khi chết.\r\n- Cây lúa bị bệnh mang virus cho đến khi gặt, lúa chét cũng có thể nhiễm bệnh. Khi bị bệnh ở giai đọan lúa non, lúa sẽ không trổ bông, năng suất  giảm nghiêm trọng hoặc mất trắng.\r\n- Rầy nâu chích hút cây lúa bệnh sau 5-10 phút là mang mầm bệnh trong cơ thể và khoảng 10 ngày sau là có thể lan truyền virus gây bệnh sang cây lúa khỏe khác.\r\n- Virus gây bệnh không truyền qua trứng rầy, đất, nước, không khí.\r\n- Rầy nâu cánh dài mang virus phát tán đi rất xa nên phạm vi lây lan của bệnh rộng, rầy cánh ngắn mang virus lây lan bệnh trong phạm vi hẹp vì không thể di chuyển xa.\r\n* Biện pháp xử lý: Bệnh vàng lùn thường phát triển và gây hại nặng sau khi ruộng lúa bị nhiễm rầy nặng. Hiện không có thuốc trị bệnh này. Cách phòng bệnh tốt nhất là quản lý tốt rầy nâu chọn Thời vụ gieo sạ “né rầy”, khi ruộng bị bệnh phải tiêu hủy cây bệnh sau đó phòng trừ rầy nâu và phun các loại phân bón lá có hàm lượng lân và kali cao nhằm tăng sức đề kháng của cây.\r\n2.6. Bệnh lùn xoắn lá:\r\n* Tác nhân:\r\nBệnh do virus Rice ragged stunt virus (RRSV) gây ra. Môi giới truyền bệnh là rầy nâu (Nilaparvata lugens), một cá thể rầy nâu mang virus gây bệnh chích hút trên cây lúa sau một vài giờ sẽ làm cây lúa bị nhiễm bệnh.\r\n* Triệu chứng:\r\n- Cây lúa bị bệnh lùn xoắn lá sinh trưởng cằn cọc, cây thấp lùn, thân lúa lùn cứng hơn bình thường, chiều cao cây, chiều dài lá, rễ, cổ áo đều bị giảm sút, co ngắn lại khoảng 40-60% so cây khỏe. Số dảnh/khóm tuy có nhiều song hầu hết không có bông hoặc trổ bông muộn, trổ bông không thoát. Lúa trổ muộn, bông lúa ngắn, ít hạt, lép lửng, hạt có nhiều đốm nâu, dẫn đến thất thu hoặc giảm năng suất nghiêm trọng.\r\n- Ở thể nhẹ lá cứng, dày và có màu xanh đậm, gân lá bị phồng, mép lá có răng cưa. Đốt thân ngắn lại, thường đâm chồi và rễ bên trong bẹ lá, thân dày cứng. Ở các đốt trên, rễ mọc ngược bên trên ở bên trong bẹ lá. Chồi phụ mọc từ các đốt trên bị cong xoắn ở trong bẹ lúa.\r\n- Ở thể nặng lá lúa ngắn, xoắn như mũi khoan, trên lá bệnh có nhiều vết đốm nâu. Lúa hoàn toàn không trổ được.\r\n- Virus gây bệnh không lây truyền qua hạt giống, nước, không khí, đất, tác nhân cơ giới mà chỉ được lây truyền qua môi giới là rầy nâu.\r\n* Điều kiện phát sinh phát triển:\r\n- Cỏ lồng vực (Echinochloa Crus-galli) và cỏ đuôi phượng (Leptochloa chinensis) là 2 loại ký chủ trung gian quan trọng của bệnh. Do đó trừ các loài cỏ này cũng góp phần hạn chế nguồn bệnh lùn xoắn lá trên đồng ruộng.\r\n- Trên ruộng lúa bệnh phát sinh sớm hay muộn là do thời gian xuất hiện của rầy mang nguồn bệnh. Số lượng cây bị bệnh nhiều cùng với mật độ rầy cao thì bệnh sẽ phát triển mạnh. Thời tiết và giống lúa thích hợp với rầy nâu cũng là điều kiện thích hợp cho bệnh phát triển.\r\n- Trường hợp rầy nâu chích hút và nhiễm cả 2 virus trên vào cơ thể rầy, sau đó chích hút trên cây khỏe sẽ truyền cùng lúc hai bệnh lùn xoắn lá và vàng lùn làm cây lúa có triệu chứng tổng hợp của cả 2 bệnh là: Bụi lúa lùn, trong bụi lúa vừa có lá vàng từ chóp lá vào vừa có lá xanh đậm và vặn xoắn.\r\n* Biện pháp xử lý: Tương tự bệnh vàng lùn hại Lúa.";
            }
            else if (s.Equals("Măng tây"))
            {
                GuideTitle = "SÂU BỆNH HẠI CÂY MĂNG TÂY VÀ BIỆN PHÁP PHÒNG TRỪ";
                GuideDescription = "1. GIỚI THIỆU CÂY MĂNG TÂY\r\n1.1. Giá trị dinh dưỡng và  kinh tế của cây măng tây\r\nMăng tây (Asparagus officinalis L.) thuộc họ Asparagaceae  là một loại rau cao cấp, có hàm lượng dinh dưỡng khá cao, gồm 83% nước và 17% chất khô; trong đó có 2,2% đạm protein, 1,2% đường glucid, 0,6% chất xơ celluloze và 21% các chất khoáng như Mg, K, Ca, Zn,... Ngoài ra, măng tây còn có tác dụng chống lão hóa, chống béo phì, làm giàu sữa mẹ và đặc biệt là giảm lượng cholesteron trong máu, giúp ổn định huyết áp. Trong ẩm thực măng tây là một loại thực phẩm cao cấp. Vì những lợi ích đó mà măng tây được giới ẩm thực gọi là “rau vua”.\r\n1.2. Đặc điểm thực vật học và yêu cầu sinh thái của cây măng tây\r\nMăng tây đặc biệt thích hợp với vùng khí hậu nhiệt đới có nhiệt độ trung bình 20oC-30oC. Măng tây là cây ưa ánh sáng. Trồng măng tây ở nơi bị che khuất, hiệu suất quang hợp thấp, cây sinh sản kém, năng suất măng sẽ giảm. Thời gian chiếu sáng thích hợp là > 7-8 giờ/ngày và vùng có lượng mưa ít (< 1.000 mm/năm). Mùa nắng phải có đủ nước tưới để giữ đều độ ẩm 60-70% trong chân đất trồng,...\r\nĐất trồng cây măng tây là các loại đất nhẹ, tơi xốp, giàu dinh dưỡng hữu cơ, đất phù sa mới bồi ven sông, đất cát pha, … Đất trồng tơi xốp, giàu dinh dưỡng hữu cơ, vi sinh có ích thì mầm măng tây càng có chất lượng tốt, năng suất cao.\r\nCây măng tây xanh thích nghi sinh trưởng phát triển tốt trên trên đất cát pha thoát nước tốt, ít mưa, nhiệt độ để cây phát triển trên 20oC, lượng mưa <800 mm/năm.\r\n2. KỸ THUẬT VƯỜN ƯƠM CÂY GIỐNG MĂNG TÂY\r\n2.1. Kỹ thuật ươm cây giống măng tây bằng viên nén xơ dừa\r\nCách gieo hạt măng tây vào viên nén:\r\n- Ngâm viên nén vào nước sạch trong 2-5 phút, viên nén sẽ nở to gấp 3-4 lần kích thước ban đầu thành bầu để gieo hạt, lấy tay gỡ phần bao gần lỗ tra hạt để chuẩn bị gieo.\r\n- Phơi hạt măng tây 2 giờ để kích thích mọc mầm, ngâm nước 12 giờ, vớt ra rửa sạch và gieo ngay vào lỗ hạt sau đó phủ kín hạt bằng cát.\r\n- Tưới nước giữ ẩm hàng ngày, che chắn khỏi gia súc hay chim phá hoại.\r\n- Không cần phải bón phân vì trong viên nén đã tích hợp dinh dưỡng nuôi cây.\r\n- Từ khi gieo đến mọc mầm là 15 ngày, đến khi cây giống mọc chồi thứ hai là 45 ngày tuổi, chăm sóc tiếp tục đến 60 ngày tuổi thì đem trồng ra ruộng sản xuất.\r\n2.2. Kỹ thuật ươm cây giống măng tây bằng bầu nilon\r\n- Bầu ươm cây giống măng tây có đường kính 6cm cao 15cm có đục lỗ sẵn. \r\n- Giá thể làm bầu gồm 1/3 đất + 1/3 xơ dừa + 1/3 phân hữu cơ ủ hoai (không dùng trấu đốt). \r\n- Phơi hạt măng tây 2 giờ để kích thích mọc mầm, ngâm nước 12 giờ, vớt ra rửa sạch và gieo hạt vào giữa bầu có giá thể, chiều sâu gieo hạt 1,5cm (1 đốt tay) sau đó phủ kín hạt.\r\n- Tưới nước giữ ẩm hàng ngày, che chắn khỏi gia súc hay chim phá hoại.\r\n- Từ khi gieo đến mọc mầm là 15 ngày, chăm sóc tiếp tục đến 90 ngày tuổi thì đem trồng ra ruộng sản xuất.\r\n2.3. Kỹ thuật làm luống chìm ươm cây giống măng tây trên đất cát \r\n- Chuẩn bị đất 1 tháng trước khi trồng: dọn sạch cỏ dại, cày phơi ải và bón phân hữu cơ (ủ hoai) trước khi gieo hạt.\r\n- Làm luống chìm tương tự ươm hạt giống rau, kích thước luống 0,8m, bờ rộng 0,2m cao 0,2m.\r\n- Dùng cây cỡ kéo hàng gieo hạt khoảng cách hàng 10cm. \r\n- Phơi hạt măng tây 2 giờ để kích thích mọc mầm, ngâm nước 12 giờ, vớt ra rửa sạch và gieo hạt vào rãnh khoảng cách gieo hạt (hàng x cây) 10cm x 6cm, sâu 1cm (1 đốt tay) và lấp kín hạt bằng trang gỗ.\r\n- Tưới nước giữ ẩm hàng ngày bằng hình thức tưới rãnh, không dùng vòi phun và không nên tưới bằng hệ thống phun mưa vì cây non rất yếu dễ bị đổ rạp chết cây.\r\n- Bón phân cho cây giống:\r\n+ Từ khi gieo hạt đến mọc mầm 15 ngày không bón phân.\r\n+ Sau gieo 30 ngày tưới phân bón lá.\r\n+ Sau gieo 45 ngày tưới phân DAP (ngâm tưới) 3-5kg/1000m2.\r\n+ Sau gieo 60 ngày tưới phân DAP (ngâm tưới) 3-5kg/1000m2.\r\n+ Sau gieo 75 ngày tưới phân DAP, dừng tưới phân trước khi nhổ cây 15 ngày.\r\n- Nhổ cây con đem trồng ra ruộng sản xuất sau khi cây khoảng 90 ngày tuổi.\r\nLưu ý: Mỗi lần bón phân, sử dụng Trichoderma tưới bổ sung vào gốc để tăng cường tính kháng các loại bệnh hại rễ cho măng tây.\r\n3. KỸ THUẬT TRỒNG MĂNG TÂY\r\n3.1. Thời vụ trồng măng tây\r\n- Gieo cuối tháng 8 đến đầu tháng 9 để trồng tháng 10, 11.\r\n- Gieo cuối tháng 2 đến tháng 4 để trồng từ tháng 4 đến tháng 6..\r\n3.2. Chuẩn bị đất trồng măng tây\r\nĐất trồng măng tây là vùng đủ điều kiện sản xuất rau quả an toàn (theo Thông tư 59/2012/TT-BNNPTNN, ngày 09/11/2012 của Bộ trưởng Bộ Nông nghiệp và Phát triển nông thôn Quy định về quản lý sản xuất rau quả, chè an toàn). Đất có độ tơi xốp cao, giàu chất hữu cơ, thoát nước tốt, có tầng canh tác dày 40-50cm, mực nước ngầm sâu dưới mặt đất 50cm, độ pH 6,5-7,5.\r\nTrước khi trồng, cày 2 lần cách nhau khoảng 10 ngày, cày sâu 20 - 25 cm, rồi bừa, xới đất 2 - 3 lần cho thật tơi xốp, sạch cỏ dại và san phẳng mặt đất trồng.\r\nLên luống (liếp) rộng 120 cm x cao 30 cm, rãnh 20 cm, tạo mặt liếp dốc nghiêng về 2 bên mép liếp. Tại vùng đất cát có thể làm luống chìm, nhưng phải có hệ thống tiêu nước tốt vào mùa mưa.\r\n\t3.3. Giống măng tây\r\nGiống măng tây xanh Atlas và Atticus hoặc các giống măng tây xanh có các đặc điểm tương tự.\r\n\t3.4. Mật độ và phương pháp  trồng\r\n- Mật độ, khoảng cách trồng: khoảng cách trồng 120 x 45 cm tương ứng với mật độ 18.500 cây.\r\n- Phương pháp trồng: Đặt cây con ngay ngắn vào hố trồng, mặt bầu ngang mặt đất. Sau đó lấy đất 2 bên mép liếp phủ một lớp đất mặt dày khoảng 5 - 10 cm để giữ cây măng đứng thẳng. Sau trồng nếu có cây bị hư hỏng, sâu bệnh hoặc chết thì tiến hành trồng dặm bổ sung ngay.\r\n3.5. Bón phân và chăm sóc\r\n* Bón phân\r\n- Bón lót trước khi trồng măng tây\r\nLượng phân cho 1 ha: 20 tấn phân chuồng kết hợp tưới Tricoderma hoặc 2 tấn phân hữu cơ vi sinh + 350 kg lân nung chảy + 65 kg Urea + 60 kg KCl. \r\nCách bón: Trên mặt liếp đào một rãnh dọc theo chiều dài liếp, rãnh rộng 50cm sâu 25 cm hoặc đào hố kích thước 40-40 cm cách nhau 45-50 cm. Đảo đều phân với đất, sau đó trồng cây ngay ngắn. \r\n- Bón thúc cho măng tây\r\n+ Bón thúc 2 lần/tháng; \r\n+ Liều lượng:\r\nTháng thứ nhất và thứ 2 bón 200 kg NPK 16.16.8; \r\nTháng thứ 3 bón 300 NPK 16.16.8; \r\nTháng thứ 4 bón lần 1: 15 tấn phân chuồng+ 65 kg U rea+ 60 kg Kcl; Lần 2: 150 kg NPK 16.16.8; \r\nTừ tháng thứ 4 trở đi mỗi tháng bón 300 kg NPK 20.20.15. \r\nMỗi lần bón phân, có thể sử dụng phân bón chứa Trichoderma tưới bổ sung vào gốc để tăng cường tính kháng các loại bệnh hại rễ cho măng tây.\r\n+ Từ năm thứ 2 trở đi: lượng phân có thể tăng lên 10-15% tùy tình hình sinh trưởng của cây. Có thể bón các loại phân khác với hàm lượng dinh dưỡng tương tự như trên. Ngoài ra, cần kết hợp sử dụng thêm các loại phân bón bổ sung các nguyên tố trung và vi lượng cho cây.\r\n           * Chăm sóc\r\n- Làm cỏ\r\n+ Ngay từ khi chuẩn bị đất trồng, cần làm và xử lý cỏ thật kỹ.\r\n+ Phải làm cỏ thường xuyên, liên tục, dứt điểm từ khi cỏ còn non, không để cỏ già rơi hạt tái sinh.\r\n+ Có thể sử dụng rơm, trấu đã xử lý mầm bệnh để phủ gốc thay việc làm cỏ.\r\n+ Không được sử dụng thuốc trừ cỏ cho cây măng tây.\r\n\t- Tưới nước\r\nSử dụng hệ thống tưới nhỏ giọt (hoặc hệ thống tưới phun mưa). Định kỳ tưới nước tùy thuộc thời tiết và độ ẩm đất, mùa nắng phải tưới thường xuyên khoảng 1 - 2 lần/ngày, 2 - 3 giờ/lần (tuỳ thuộc vào độ ẩm đất), mùa mưa có thể tưới ít hơn, đảm bảo duy trì độ ẩm ở mức 65 - 70%. Tưới vào buổi sáng sớm và chiều mát; không tưới nước cho măng sau 17h hàng ngày.\r\nSử dụng thiết bị đo độ ẩm và pH đất DM15 để quản lý độ ẩm đất trong quá trình tưới nước nhằm duy trì độ ẩm ở mức 65 - 70%.\r\n- Cắm cọc, giăng dây chống đổ ngã\r\n+ Sau khi trồng 10 ngày, cắm hai đầu hàng 2 trụ sắt chữ T (cọc trụ thép V3 và thanh ngang thép mã kẽm vuông 2 x 2 cm), ở giữa hàng cắm các trụ sắt chữ T (cọc trụ thép V2 và thanh ngang là thép mã kẽm vuông 1,5 x 1,5 cm); trụ cao 1,2m; thanh ngang 0,5m; trụ chôn sâu 0,3m; trụ cách trụ 3 - 4 m. Dùng dây cước nilon cỡ 10mm buộc nối các trụ với nhau, cách bề mặt luống khoảng 40 - 50 cm và tiến hành nâng dây cước dần theo sự sinh trưởng của cây măng. Sử dụng dây nilon buộc cố định cây măng vào dây cước để tránh đổ ngã. Khi cây măng tây đã lớn, sử dụng lưới giăng rò ô vuông 10 cm x 10 cm (chiều ngang lưới 5 ô vuông) dăng lưới ở độ cao 90 - 100 cm để cây măng luồn vào ô lưới, giữ cây luôn đứng thẳng.\r\n+ Ngoài ra, trên cùng hàng cây trồng, chen giữa các cây măng, có thể tiến hành cắm các cọc tre đường kính khoảng 5 cm, cao khoảng 120 cm, cách nhau 3-4 m. Dùng dây cước nilon chắc chắn giăng thành hàng đôi (kẹp cây măng ở giữa), cách mặt liếp ở độ cao 50 cm; rồi giăng thêm dây hoặc nâng dần đôi dây lên cao khoảng 75 cm, 90 cm, 100 cm tuỳ theo độ cao lớn của cây để giữ cây luôn đứng thẳng.\r\n\t- Tỉa bớt cây yếu và dưỡng cây mẹ: Từ khi trồng cho đến khi cây được 135 ngày, định kỳ 20 ngày tỉa bỏ bớt cây yếu, cây già và cây bị sâu, bệnh giữ cho bụi măng từ 4 - 5 cây mẹ khoẻ mạnh để cho măng.\r\n- Cắt ngọn, tỉa chồi, giữ cây mẹ khi thu hoạch: Sau trồng 135 ngày (4,5 tháng), quan sát thấy đường kính gốc thân cây mẹ đạt > 10 - 12 mm, lá chuyển sang màu xanh đậm tiến hành cắt hạ bớt ngọn cây măng ở độ cao khoảng 1,2 m để giúp cây mẹ phình to gốc và tăng lượng cành lá quang hợp cho cây, kích thích mạnh việc trổ măng. Sau khi cắt ngọn, tiến hành tỉa chồi và giữ lại từ 4 - 5 cây mẹ khoẻ mạnh để cho măng.\r\n3.6. Sâu bệnh hại măng tây và cách phòng trừ\r\n\tÁp dụng các biện pháp quản lý dịch hại cây trồng tổng hợp (IPM) \r\n\t- Sâu hại\r\n+ Phòng trừ sâu xanh da láng (Spodoptera exigua)\r\n* Biện pháp canh tác: Vệ sinh đồng ruộng, làm sạch cỏ dại trên ruộng măng tây và quanh bờ để hạn chế nguồn ký chủ phụ của sâu.\r\n* Biện pháp sinh học: Hạn chế phun thuốc để bảo tồn thiên địch trên đồng ruộng như nhện, bọ rùa, ong ký.\r\n* Biện pháp hoá học: Sử dụng luân phiên các loại hoạt chất Abamectin, Emamectin benzoate.. ..để phòng trừ\r\n+ Phòng trừ sâu khoang (Spodoptera litura)\r\n\t* Biện pháp canh tác: Vệ sinh đồng ruộng, làm sạch cỏ dại trên ruộng măng tây và quanh bờ để hạn chế nguồn ký chủ phụ của sâu\r\n\t* Biện pháp sinh học: Hạn chế phun thuốc để bảo tồn thiên địch trên đồng ruộng như nhện, bọ rùa, ong ký sinh. Ngoài ra, có thể sử dụng bẫy bả chua ngọt để thu hút bướm khi phát triển rộ\r\n\t* Biện pháp hoá học: Có thể sử dụng thuốc BVTV để phòng trừ.\r\n+ Phòng trừ bọ trĩ (Thrips ssp)\r\n* Biện pháp canh tác: Vệ sinh đồng ruộng, làm sạch cỏ dại trên ruộng măng tây.\r\n\t* Biện pháp sinh học: Hạn chế phun thuốc để bảo tồn thiên địch trên đồng ruộng như bọ rùa, bọ xít ăn bọ trĩ.\r\n\t* Biện pháp hoá học: Sử dụng luân phiên các loại hoạt chất Spinetoram, Fipronil, Imidacloprid để phòng trừ.\r\n- Bệnh hại măng tây\r\nTrên cây măng tây thường xuất hiện các loại bệnh hại chính gồm bệnh đốm tím, bệnh thán thư, bệnh cháy thân và bệnh gỉ sắt các biện pháp phòng trừ như sau:\r\n+ Biện pháp canh tác: Bổ sung đầy đủ và cân đối dinh dưỡng để cây phát triển khỏe mạnh. Vệ sinh thường xuyên ruộng măng tây, loại bỏ những cây bị bệnh và đem ra khỏi vườn tiêu hủy ngay khi bệnh vừa mới chớm xuất hiện. Cắt tỉa cây chết, cành nhánh ở phần gốc, giữ cho vườn trồng luôn thông thoáng.\r\n+ Biện pháp hoá học: Sử dụng thuốc BVTV để phòng trừ. Liều lượng và cách sử dụng theo khuyến cáo trên bao bì.\r\nChú ý: Khi dùng thuốc bảo vệ thực vật, cần phải đọc kỹ hướng dẫn, bảo đảm thực hiện theo nguyên tắc “4 đúng”, nhất là phải bảo đảm thời gian cách ly ít nhất 10 ngày trước khi thu hoạch đúng như quy định của từng loại thuốc bảo vệ thực vật.\r\n           3.7. Thu hoạch, sơ chế và bảo quản măng tây thương phẩm\r\n- Thu hoạch măng tây\r\nTừ khi trồng đến khi có măng thu hoạch khoảng 4 - 4,5 tháng tùy theo giống và điều kiện trồng trọt. Lứa đầu có thể thu hoạch măng trong vòng 1 tháng. Tiếp đến cho nghỉ dưỡng cây 1 tháng, tỉa bỏ 1 lần chừa lại cây lớn. Đến tháng thứ 6 thì thu hoạch măng hàng hóa bình thường. Đến giai đoạn kinh doanh, măng tây xanh cho thu hoạch liên tục trong vòng 2 - 3 tháng rồi nghỉ một tháng để dưỡng cây, một năm măng tây xanh có thể thu hoạch trong vòng 8 - 9 tháng.\r\n+ Kỹ thuật thu hoạch:\r\n\t* Chiều dài măng thu hoạch: Khi các chồi măng nhú lên cao khỏi mặt đất khoảng 25 - 28 cm là lúc cần phải thu hoạch ngay để có được sản phẩm măng chất lượng cao.\r\n\t* Thời gian thu hoạch măng tây xanh là buổi sáng, thường từ 4-6 giờ sáng mỗi ngày, trước khi mặt trời mọc để măng tránh tiếp xúc với ánh nắng.\r\n\t* Kỹ thuật thu: dùng tay nắm chặt gốc chồi măng, nghiêng 30-450C giật nhẹ, chồi măng sẽ tách rời khỏi rễ trụ dễ dàng. Sau khi thu hoạch măng, cần phải nén chặt đất trồng nơi đã lấy măng.\r\n+ Dưỡng cây sau khi thu hoạch:\r\nKhi cây măng tây có dấu hiệu già hoá khoảng 2 - 3 tháng thu hoạch liên tục hàng ngày. Cây mẹ bị suy yếu, tàn lụi, bệnh hại phát sinh nhiều thì phải dừng thu hoạch để dưỡng cây. Trong thời gian này phải chăm sóc tốt, bón đủ phân, tỉa bỏ cây yếu, cây bệnh và phòng trừ bệnh triệt để trong vòng 1 tháng. Sau đó tiếp tục chu kỳ thu hoạch kế tiếp.\r\n- Sơ chế và phân loại măng tây\r\nSau khi thu hoạch, cần tiến hành sơ chế phân loại, rửa sạch, cắt cỡ, bó thành bó xong có thể phân phối ra thị trường hoặc tiến hành đưa vào bảo quản.\r\n- Bảo quản măng tây thương phẩm\r\nMăng tây sau khi thu hoạch, làm sạch, để khô không dính nước sau đó sử dụng túi bảo quản trái cây (Túi Green MAP) bọc kín và bảo quản ở nhiệt độ 20C giúp sản phẩm măng tây thương phẩm bảo quản được từ 20 - 25 ngày.";
            }
            else if (s.Equals("Hành tím"))
            {
                GuideTitle = "PHÒNG TRỪ SINH VẬT GÂY HẠI CÂY HÀNH TỎI";
                GuideDescription = "1. SÂU HẠI\r\n1.1. Sâu xanh da láng: (Spodoptera exigua).\r\n* Nhận dạng: Bướm nhỏ, màu nâu và có 1 đốm vàng ở giữa cánh rất đặc sắc. Sâu non có màu xanh lục với 2 sọc vàng nâu chạy dọc 2 bên thân mình, sâu tuổi lớn có màu xanh nhạt, dễ lẫn với màu của cọng hành.Trứng đẻ thành từng ổ từ 20-30 trứng có phủ lông trắng.\r\n* Tập quán sinh sống và gây hại:\r\n- Bướm hoạt động mạnh vào ban đêm, trứng đẻ trên lá. Một bướm cái có thể đẻ 500-800 trứng.\r\n- Sâu non đục lổ nhỏ chui vào bên trong ăn phần xanh của lá. \r\n- Khi sâu còn nhỏ trong một lá có thể có hàng chục con, khi lớn chúng phân tán dần sang các lá khác. Loài sâu này cắn phá mạnh làm lá bị khô héo, chết, gẫy gập, xơ xác, cả bụi trở nên vàng úa, còi cọc cả ruộng bị trắng xoá, tàn lụi. Sâu thường phát sinh và gây hại nhiều trong điều kiện thời tiết nóng, ít mưa.\r\n- Vòng đời trung bình 30-40 ngày.\r\n* Biện pháp xử lý:\r\n- Vệ sinh đồng ruộng, phát hiện sớm khi sâu còn nhỏ để phòng trừ.\r\n- Biện pháp hóa học: Sử dụng một số hoạt chất bảo vệ thực vật để phun phòng trừ.\r\n1.2. Dòi đục thân lá:(Delia Antiqua).\r\n* Nhận dạng:\r\n- Trứng có màu trắng và dài và được xếp thành từng nhóm trên thân, lá, củ của cây ký chủ và gần mặt đất, trong các kẽ đất.\r\n- Ấu trùng: Sâu non được gọi là dòi, dòi dài khoảng 02 mm, màu trắng trong, phần trước hơi vàng, trông rõ ruột bên trong màu đen. Thời gian sống của dòi khoảng 3-4 ngày.\r\n- Nhộng màu nâu vàng, dài khoảng 1,5 mm, rộng 0,7 mm. Thời gian phát dục từ 6-8 ngày. Nhộng thường phân bố ở vị trí cuối cuống lá hoặc dưới mặt đất.\r\n- Trưởng thành: Ruồi có kích thước từ 3-5 mm, chân màu đen, đôi cánh trong suốt, mắt kép màu nâu.\r\n* Tập quán sinh sống và gây hại:\r\n- Con cái đẻ trứng dài, trắng gần gốc cây, trong các kẽ đất. Trứng nở trong 2-7 ngày. Sâu non sau khi nở bò vào vỏ bọc lá và đi tới phần ống hoặc bẹ lá. Sâu non ăn bẹ lá và phát triển đầy đủ trong 2-3 tuần, sâu non thường tập trung cắn phá củ và bẹ lá, 50 con dòi có thể ăn hết 1 bẹ lá lớn.\r\n- Dòi đẫy sức bò ra khỏi bẹ lá và hóa nhộng trong đất. Sau 2-3 tuần, trưởng thành và bắt đầu thế hệ mới. Trong thế hệ thứ ba, dịch hại thường tấn công phần củ ngay trước khi thu hoạch.\r\n- Sâu đục vào củ ăn các mô thịt trong lòng của củ hành, tỏi, làm cho cây bị tổn thương, không dẫn được nước, chất dinh dưỡng, tạo cơ hội cho các vi khuẩn, nấm xâm nhập củ gây thối củ, thối rễ, úa lá, chết cây. Sâu hại cả sau khi thu hoạch và bảo quản trong kho.\r\n* Biện pháp xử lý:\r\n- Vệ sinh đồng ruộng, xới xáo, phơi đất dưới nắng, làm sạch cỏ trước khi xuống giống.\r\n- Luân canh với các loại cây trồng khác họ.\r\n- Ngắt bớt những lá bị ruồi đục đem chôn để giảm bớt nguồn sâu, nhất là các lá già.\r\n- Dùng bẫy dính màu vàng để bẫy ruồi trưởng thành.\r\n- Biện pháp hóa học: Sử dụng các thuốc có tính thấm sâu và nội hấp như: Emamectin benzoate (Tasieu 1.9EC, 5WG),… để phun phòng trừ.\r\n1.3. Bọ trĩ: (Thrips tabaci Lindeman).\r\n* Nhận dạng: Bọ trĩ trưởng thành và bọ trĩ non thường rất nhỏ, có kích thước nhỏ hơn 01 mm. Sâu non có màu vàng hoặc trắng. Con già có màu vàng nâu và di chuyển nhanh. Chúng dùng 2 răng cửa giũa cho rách biểu bì lá cây để hút nhựa.\r\n* Tập quán sinh sống và gây hại:\r\n- Con cái đẻ trứng vào trong mô lá (khoảng 80 trứng/con), sau 5-10 ngày trứng sẽ nở, vòng đời hơn 21 ngày tùy theo môi trường, nhiệt độ.\r\n- Lá bị hại có màu sáng bạc và có vết hoặc đốm nhỏ màu nâu. Lá có thể héo hoặc biến dạng. Ngọn của các lá phía ngoài có màu nâu. Trong trường hợp bị hại nghiêm trọng lá rũ xuống, củ nhỏ và biến dạng. \r\n- Khí hậu lạnh, con trưởng thành có thể ngừng hoạt động và ngủ đông trong đất, khi nhiệt độ ấm lên chúng hoạt động trở lại. Bọ trĩ phát triển thích hợp trong điều kiện thời tiết nóng và khô.\r\n* Biện pháp xử lý:\r\nCó thể sử dụng thuốc Emamectin benzoate (Tasieu 1.9EC, 5WG),….đã đăng ký trên cây rau màu để phòng trừ. \r\n2. BỆNH HẠI\r\n2.1. Bệnh đốm lá:\r\n* Tác nhân: Do nấm Cercospora sp. gây hại.\r\n* Triệu chứng:\r\nĐốm bệnh tạo thành từng vòng trên lá, các vết bệnh có thể lan rộng và làm thủng lá, bệnh nặng làm giảm năng suất và chất lượng rõ rệt.\r\n* Điều kiện phát sinh, phát triển:\r\nNấm phát sinh và gây hại mạnh nhất trong thời tiết ẩm và nhiệt độ thấp. Khi bị mưa nặng hạt là điều kiện thuận lợi cho bệnh lây nhiễm.\r\n* Biện pháp xử lý:\r\n- Cần dọn sạch cỏ dại, tàn dư cây trồng sau khi thu hoạch. Không nên trồng quá dày và trên đất khó thoát nước. \r\n- Biện pháp hóa học: Sử dụng các thuốc có hoạt chất như: Chlorothalonil (Daconil 75WP), Polyoxin complex (Polyoxin AL 10WP), Propineb (Antracol 70WP),… để phòng trừ.\r\n2.2 Bệnh đốm vòng:\r\n* Tác nhân: Do Alternaria porri. gây hại.\r\n* Triệu chứng:\r\nVết bệnh là những hình ovan, đồng tâm. Lúc đầu là những đốm nhỏ trắng sau đó nếu thời tiết ẩm vết bệnh chuyển màu xám hay nâu. Nếu bị hại nặng lá sẽ bị khô, cây chết. Đôi khi ở phần dưới cây sát mặt đất có thể cũng bị hư hại do thối ướt sau đó khô lại và củ cũng bị khô theo.\r\n* Điều kiện phát sinh, phát triển:\r\nNếu cây bị bệnh ở thời kỳ sớm có thể ảnh hưởng nghiêm trọng đến năng suất. Trong thời kỳ bảo quản nấm xâm nhập vào củ và gây thối. Bệnh phát triển và gây hại mạnh trong điều kiện ẩm độ cao và nhiệt độ từ 20-300C. Nấm bệnh có thể tồn tại ở những tàn dư cây bệnh, bào tử sẽ phát tán theo gió và nước bắn lên lá.\r\n* Biện pháp xử lý:\r\n- Chọn giống không bị nhiễm bệnh để trồng. Thu dọn tàn dư cây bệnh sau khi thu hoạch. Làm đất kỹ, trồng mật độ vừa phải. \r\n- Sử dụng một số loại thuốc có hoạt chất sau để phòng trừ: Azoxystrobin + Fenoxanil (Omega 325WP, 400SC),…….\r\n2.3. Bệnh sương mai:\r\n* Tác nhân:Do nấm Peronospora schleidni. gây hại.\r\n* Triệu chứng:\r\nLá già bị bệnh có màu xanh nhạt, có lớp tơ nấm màu trắng che phủ lên vết bệnh sau đó tơ nấm chuyển sang màu xanh hơi đỏ. Bệnh nặng làm lá bị gẫy và chết. Cuống lá, vết bệnh đầu tiên có hình elip sau đó kéo dài ra, lúc đầu có màu vàng sau đó có màu nâu. Trên cây còn nhỏ ít bị bệnh gây hại, ở cây lớn các lá già bị hại trước sau đó lan dần đến củ, cây còn ít lá, củ nhỏ và cây chết.\r\n* Điều kiện phát sinh, phát triển:\r\nNấm tồn tại trong củ, trong thân cây bệnh, phát sinh, phát triển mạnh trong điều kiện nhiệt độ thấp hơn 220C và độ ẩm cao sương mù.Nhiệt độ cao và ẩm độ thấp hạn chế sự phát triển của bệnh.\r\n* Biện pháp xử lý:\r\n- Sử dụng giống tốt, chọn củ giống sạch bệnh vì một số loại nấm bệnh có khả năng lan truyền qua củ giống.\r\n- Luân canh với cây trồng; vệ sinh đồng ruộng, thu dọn sạch tàn dư cây bệnh đem tiêu hủy sau khi thu hoạch; không trồng hành, tỏi những nơi kém thoát nước.\r\n- Biện pháp hóa học: sử dụng một số loại thuốc sau để phòng trừ: Iprovalicarb + Propineb (Melody duo 66.75WP), Kresoxim-methyl (Sosim 300SC), Propineb (Antracol 70WP, Tadashi 700WP)...\r\n2.4. Bệnh thối nhũn:\r\n* Tác nhân: Do vi khuẩn Erwinia sp. gây hại.\r\n* Triệu chứng: Bệnh xuất hiện khi cây bắt đầu hình thành củ, vi khuẩn xâm nhập chủ yếu từ rễ lên củ và từ ngọn xuống củ. Vết bệnh ban đầu có dạng giọt dầu nằm trong mô củ, sau đó kéo dài ra ăn sâu vào thịt củ và bẹ lá. Nếu bị sớm cây, lá vàng úa giống như bị ngập nước, cây còi cọc. Nếu cắt ngang củ sẽ thấy các vết thâm đen có đường đồng tâm theo thân giả, bóp nhẹ sẽ thấy các giọt dịch vi khuẩn màu kem. Nếu cắt dọc củ sẽ thấy vết bệnh xâm nhập vào nõn tạo ra các đường màu thâm đen chạy dọc mô củ. Mô củ bị thối rữa.\r\n* Điều kiện phát sinh, phát triển:\r\n- Nguồn bệnh tồn tại chủ yếu trong củ và tàn dư lá bệnh, trong đất. Trong điều kiện khô hạn vi khuẩn có thể tồn tại đến 24 tháng.\r\n- Vi khuẩn E. carotovora rất mẫn cảm với lượng đạm tự do trong thân, củ hành tỏi.\r\n- Trong điều nóng ẩm của kho bảo quản thì bệnh phát sinh nặng, gây tổn thất nghiêm trọng. \r\n* Biện pháp xử lý:\r\n- Chọn củ hành tốt, không bị nhiễm bệnh để làm giống, trồng đúng mật độ. Bón phân cân đối là biện pháp quan trọng hạn chế sự phát triển của bệnh, hạn chế bón thừa đạm.\r\n- Sử dụng các loại hoạt chất như: Gentamycin sulfate + Streptomycin Sulfate (Panta 66.6WP),… để phun phòng trừ.\r\n2.5. Tuyến trùng hại rễ, thân cây hành:\r\n* Triệu chứng:\r\n- Tuyến trùng không thể nhìn thấy bằng mắt thường mà phải quan sát chúng thông qua kính hiển vi (kích thước chỉ từ 0,5-2 mm). Chúng sống và di chuyển qua mô tế bào cây trồng, chích hút, bơm các độc tố vào rễ cây làm rễ bị nghẽn mạch, phình to tạo nên các khối u sần hoặc bị hoại tử khiến cho khả năng hút nước và dinh dưỡng của cây bị giảm, cây sinh trưởng kém vàng lá và chết. Triệu chứng này nặng hơn khi kết hợp với nấm bệnh, vi khuẩn xâm nhập qua vết thương trên rễ do tuyến trùng gây ra.\r\n- Bộ phận tuyến trùng tấn công trực tiếp là rễ cây trồng. Chúng ký sinh trong các tế bào rễ (làm tổ trong rễ) nên làm cho các rễ bị biến dị tạo các u bướu - nông dân thường hay gọi là “bệnh ung thư”. Rễ cây trồng bị tuyến trùng tấn công còn bị tổn thương tạo nhiều nhánh và đỉnh rễ hoại tử. Do bộ rễ bị tổn thương như vậy nên trên thân lá sẽ xuất hiện những triệu chứng như lá biến vàng, sinh trưởng giảm và thiếu sức sống,…\r\n* Điều kiện phát sinh, phát triển:\r\nTuyến trùng tồn tại và sinh trưởng phụ thuộc vào nhiều yếu tố: Độ ẩm đất trồng, số lượng rễ cây, kết cấu đất, độ pH và oxy trong đất,…Tuyến trùng khó có thể tồn tại ở đất khô nhưng có thể sống được trong đất có độ ẩm 100% (loài Meloidogyne). Nếu rễ cây phát triển mạnh thì tuyến trùng có mật độ cao và ngược lại; đất có kết cấu sét nhiều thì tỷ lệ tuyến trùng ít hơn đất cát; đất có pH thấp (đất chua) mật độ tuyến trùng nhiều,…\r\n* Biện pháp xử lý:\r\n- Cần áp dụng các biện pháp canh tác là chủ yếu như luân canh, xen canh cây trồng, dùng giống sạch bệnh, giá thể làm bầu cây cần xử lý đảm bảo không có mầm mống bệnh,…\r\n- Tiêu hủy các cây bị bệnh nhất là bộ rễ cần phải được dọn sạch. Bón phân cân đối cho cây trồng. Cần ưu tiên nguồn phân chuồng để bón cho cây nhằm duy trì hệ vi sinh vật có ích và làm kết cấu đất được tốt hạn chế tuyến trùng tồn tại, gây hại.\r\n- Không lạm dụng phân hóa học đa lượng (đạm, lân, kali) để thúc cây lớn nhanh, bón bổ sung các dinh dưỡng trung, vi lượng cần thiết cho cây trồng,...\r\n- Nếu đã xác định có mặt tuyến trùng trong đất và gây hại cây trồng cần khẩn trương tiêu hủy cây bị bệnh và xử lý đất trồng bằng một trong các loại thuốc hóa học sau: Carbosulfan (Vifu-Super 5GR, Marshal 5GR),… (liều lượng và nồng độ theo khuyến cáo của từng hãng SX).";
            }
            else if (s.Equals("Ngô"))
            {
                GuideTitle = "SÂU BỆNH HẠI CÂY NGÔ (BẮP) VÀ BIỆN PHÁP PHÒNG TRỪ";
                GuideDescription = "Ưu tiên áp dụng Quản lý dịch hại tổng hợp (IPM) để phòng chống sâu bệnh hại trên cây ngô:\r\n\t- Xới xáo, làm đất sau khi thu hoạch để diệt trứng, nhộng sâu hại\r\n\t- Bảo vệ thiên địch và vi sinh vật có ích để khống chế sâu bệnh hại.\r\n\t- Áp dụng tốt biện pháp sử dụng giống và canh tác đã nêu ở các phần trên.\r\n\t- Khi sử dụng thuốc BVTV phải tuân thủ nguyên tắc “4 đúng”, ưu tiên thuốc có nguồn gốc sinh học để quản lý sâu bệnh hại.\r\n\t1. SÂU HẠI\r\n\t1.1. Sâu xám (Agrotis ypsilon).\r\n\t* Nhận dạng:\r\n\t- Trưởng thành có kích thước trung bình (thân dài 20-25 mm, sải cánh 43-48mm). Thân màu nâu tối, nhiều lông. Râu đầu ở con cái hình sợi chỉ, con đực hình răng lược kép.\r\n\t- Trứng hình cầu hơi dẹt, màu trắng sữa sau chuyển màu hồng. Lúc sắp nở màu tím thẫm.\r\n\t- Sâu non đẫy sức dài 40-47 mm màu xám đất, trên lưng mỗi đốt có 4 chấm đen, phía dưới bụng màu nhạt.\r\n\t- Nhộng màu nâu cánh dán nằm trong đất.\r\n\t* Tập quán sinh sống và cách gây hại:\r\n\t- Trưởng thành ban ngày ẩn nấp, ban đêm đẻ trứng, thích mùi vị chua ngọt, đẻ trứng rời rạc từ 1-3 trứng trên mặt đất hoặc cỏ dại, một bướm cái đẻ 800-1.000 trứng.\r\n\t- Sâu non mới nở gặm biểu bì lá cây; sâu tuổi lớn có tính giả chết ban ngày sống dưới đất, ban đêm bò lên cắn đứt gốc cây con, kéo cây lấp miệng lổ.\r\n\t- Sâu xám hoạt động mạnh ở đất thịt nhẹ hoặc cát pha, tơi xốp thoáng, dễ thoát nước, gây hại mạnh ở những ruộng nhiều cỏ.\r\n\t- Sâu non tuổi nhỏ thường ăn nhu mô lá và cắn thủng lá, sâu non tuổi lớn thường cắn đứt gốc cây con khi cây bắp có 5-6 lá và trú ẩn ở dưới đất để ăn.\r\n\t* Biện pháp phòng trừ:\r\n\t- Cày phơi ải, bừa đất kỹ trước khi gieo trồng diệt trứng, sâu và nhộng.\r\n\t- Luân canh cây trồng, tốt nhất là với lúa nước.\r\n\t- Ở những vùng đất thường bị hại nặng trước khi gieo hạt làm bẫy bả chua ngọt để diệt bướm hoặc bắt sâu bằng tay. \r\n\t- Sử dụng thuốc hoá học khi thật cần thiết. Có thể sử dụng các loại thuốc như: Vibam 5GR, Vifu-super 5GR, Faifos 5GR,… rãi vào đất. Liều lượng theo hướng dẫn trên nhãn thuốc.\r\n\t1.2  Sùng đất/sùng trắng (Anomala antiqua).\r\n\t* Nhận dạng:\r\n\t- Thành trùng là 1 loài bọ hung màu xám xanh lục sáng.\r\n\t- Trứng nhỏ, màu trắng, thường được đẻ dưới đất nơi ẩm ướt hoặc nơi cỏ mọc nhiều.\r\n\t- Sâu non màu trắng, đầu nâu, có 2 ngàm cứng rất bén, thân có nhiều lông mịn khi đụng đến thì cong hình chữ C.\r\n\t- Nhộng hóa dưới đất.\r\n\t* Tập quán sinh sống và cách gây hại:\r\n\t- Sâu non (sùng) phá hại nhiều nhất, cắn phá rễ làm cây suy yếu hoặc chết.\r\n\t- Thành trùng ăn khuyết từng mảng lá, khi mật độ cao có thể ăn trụi lá.\r\n\t* Biện pháp phòng trừ: Tương tự như đối với sâu xám.\t\r\n\t1.3. Sâu đục thân (Ostrinia furnacalis).\r\n\tSâu đục thân thường gây hại nhiều trên cây ngô, ngoài ra nó còn gây hại trên các loại cây khác như: Bông vải, kê, đay, cà và một số cây cỏ làm thức ăn gia súc,...\r\n* Nhận dạng:\r\n- Bướm nhỏ, sải cánh 20-23 mm, màu vàng nhạt, cánh trước có 2 đường vân màu nâu hình gợn sóng chạy từ mép trước đến mép sau cánh.\r\n- Trứng tròn nhẵn xếp thành ổ 2-3 hàng theo hình vảy cá.\r\n- Sâu non mình thon mảnh, màu vàng nhạt, khi lớn dọc thân có 4 sọc nâu, đẫy sức dài 22-25 mm.\r\n- Nhộng màu nâu đỏ nằm trong thân cây.\r\n* Tập quán sinh sống và cách gây hại:\r\n- Bướm hoạt động ban đêm, ưa ánh sáng đèn. Đẻ trứng mặt dưới phiến lá gần gân chính. Con cái đẻ 100-200 trứng.\r\n- Sâu non mới nở đục nõn ngô, khi lá nở ra tạo thành một hàng lổ thủng trên phiến lá. Sâu tuổi lớn đục vào thân cây, khi có trái sâu có thể đục vào bắp ăn hạt và lõi. Sâu non có 5 tuổi, khi đẫy sức (sâu dài khoảng 22-28 mm) sâu hóa nhộng ở ngay đường đục trong thân cây hoặc trong bẹ lá, lõi bắp, lá bao.\r\n- Vòng đời trung bình 35-40 ngày (trứng 5-6 ngày, sâu con 20-25 ngày, nhộng 7-10 ngày, bướm đẻ trứng 1-2 ngày). Sâu đục thân ưa thích nhiệt độ tương đối cao, khoảng 25-300C, ẩm độ trên 80%. Sâu thường phá hại khi cây ngô đã lớn, từ khi có loa kèn, nhất là từ khi trổ cờ trở đi. Các giống ngô ăn tươi thường bị hại nặng hơn.\r\n* Biện pháp phòng trừ:\r\n- Thu hoạch xong nên dọn sạch ruộng ngô vì sâu và nhộng vẫn còn tồn tại trong thân cây ngô trong thời gian dài sau khi thu hoạch.\r\n- Gieo trồng tập trung và đồng loạt.\r\n- Sau vụ ngô nên luân canh với loại cây họ đậu không phải là ký chủ của loài sâu này để cắt đứt nguồn thức ăn của sâu.\r\n- Sử dụng thuốc hoá học khi thật cần thiết. Khi mật độ sâu cao có thể dùng các loại thuốc như: Faifos 5GR, Binhdan 10GR,… rắc vào loa kèn, nách lá hoặc phun Virtako 40WG, Proclaim®1.9EC,…\r\n1.4. Sâu đục trái (Helicoverpa armigera).\r\n* Nhận dạng:\r\n- Bướm màu nâu vàng, sải cánh 25-30 mm, cánh trước có những vân màu nâu thẫm.\r\n- Trứng hình cầu, màu trắng sữa, khi gần nở chuyển màu đen.\r\n- Sâu non màu xanh hoặc nâu nhạt, có một đường sọc màu sẫm chạy dọc thân.\r\n- Nhộng màu nâu đỏ, nằm trong trái hoặc trong đất.\r\n* Tập tính sinh sống và cách gây hại:\r\nBướm hoạt động ban đêm, đẻ trứng rải rác từng quả trên ngọn và lá non, mỗi con cái đẻ trung bình 1.000-1.500 trứng. Sâu non tuổi nhỏ gặm lấm tấm chất xanh của lá non, tuổi lớn đục vào trong bắp ăn hạt và lõi.\r\n* Biện pháp phòng trừ:\r\n- Thu hoạch xong nên dọn sạch ruộng ngô vì sâu và nhộng vẫn còn tồn tại trong thân cây ngô trong thời gian dài sau khi thu hoạch.\r\n- Gieo trồng tập trung và đồng loạt.\r\n- Sử dụng thuốc hoá học khi thật cần thiết. Khi mật độ sâu cao có thể dùng các loại thuốc để phun như: Decis 2.5EC, Virtako® 40WG, Proclaim® 1.9EC, Padan 95SP,… \r\n1.5. Sâu keo mùa thu\r\n* Nhận dạng:\r\n- Trứng có hình cầu, đường kính 0,75 mm. Trứng mới đẻ có màu xanh, sau đó chuyển sang màu trắng sữa, trước khi nở chuyển sang màu nâu nhạt.\r\n- Sâu non có 6 tuổi, tùy theo môi trường thức ăn, sâu non có màu nâu nhạt - xanh đen, với các sọc dọc thân. Kích thước sâu non tuổi 1 dài khoảng 0,5 mm, lên tuổi 3 sâu non dài 6-9 mm; tuổi 6 đẫy sức sâu non dài 30-40 mm. Trên trán sâu non tuổi lớn nhìn rõ hình chữ Y ngược màu vàng, mặt lưng màu đen với lông cứng dài. Trên mặt lưng, đốt bụng cuối có bốn đốm đen được sắp xếp thành hình vuông, trong khi các đốt khác có 4 đốm đen xếp thành hình thang.\r\n- Nhộng sâu keo mùa thu dạng nhộng bọc, màu nâu cánh gián sáng bóng. Nhộng đực dài 13-15 mm còn nhộng cái dài 16-17 mm. Đốt bụng cuối cùng có 2 gai.\r\n- Trưởng thành đực có chiều dài trung bình 16 mm, sải cánh trung bình 37mm. Phần cánh trước lốm đốm nâu nhạt, xám với một đốm hình bầu dục màu xám trắng - vàng rơm. Cánh trước của trưởng thành cái không có hoa văn rõ ràng.\r\n* Tập tính sinh sống và cách gây hại:\r\n- Trưởng thành sống trung bình 12-14 ngày, hoạt động về ban đêm. Từ khi vũ hóa đến đẻ trứng có thể bay nhiều ki-lô-mét để tìm nơi đẻ trứng, chúng có thể di chuyển xa hàng trăm ki-lô-mét nhờ gió.\r\n- Trưởng thành đẻ trứng vào ban đêm, đẻ thành ổ xếp thành hai lớp trứng là chủ yếu. Mỗi ổ trứng khoảng 100-200 quả được bao phủ bởi một lớp lông màu hồng - xám. Một trưởng thành cái có sức đẻ từ 1.000-2.000 trứng. Vị trí ổ trứng thường ở mặt trên của phiến lá hoặc cạnh cuống lá. Thời gian trứng nở sau 2-10 ngày, trung bình 2-4 ngày ở nhiệt độ 20-300C.\r\n- Sâu non là giai đoạn gây hại của sâu keo mùa thu. Thời gian pha sâu non kéo dài 14-21 ngày, nếu độ ẩm cao, nhiệt độ thấp thì thời gian pha sâu non kéo dài khoảng 30 ngày.\r\n- Nhộng vũ hóa phần lớn trong đất ở độ sâu 2-8 cm, một số ít trường hợp bắt gặp hóa nhộng giữa các lá, nách bẹ lá của cây ký chủ. Thời gian pha nhộng 7-13 ngày.\r\n- Pha sâu non gây hại trên cây trồng, sâu non tuổi 1-2 ăn biểu bì ở mặt dưới của lá non, gây ra các vết hình vuông hoặc hình chữ nhật màu trắng đặc trưng. Sâu non tuổi lớn hơn ăn khuyết lá, bẹ lá tạo thành các lổ lớn như “cửa sổ”.\r\n* Biện pháp phòng trừ:\r\n- Làm sạch cỏ dại xung quanh vườn trồng ngô để hạn chế nơi trú ẩn của sâu.\r\n- Làm đất rồi phơi đất khô để ấu trùng, nhộng trong đất chết hoặc dễ dàng bị thiên địch tiêu diệt.\r\n- Luân canh ngô - lúa nước ngay sau vụ ngô để diệt nhộng trong đất. Làm đất kỹ cũng góp phần diệt nhộng trong đất.\r\n- Thường xuyên kiểm tra đồng ruộng, đặc biệt ở giai đoạn ngô 3-6 lá để phát hiện ổ trứng, ngắt tiêu hủy.\r\n- Hạn chế sử dụng thuốc BVTV hóa học để bảo vệ thiên địch của sâu keo mùa thu.\r\n- Sử dụng chế phẩm nấm xanh, nấm trắng, vi khuẩn Bt, virus NPV để phun trừ khi sâu tuổi nhỏ.\r\n- Nhân thả ong ký sinh trứng (ong mắt đỏ,...), các loài bắt mồi ăn thịt như bọ đuôi kìm để kiểm soát sâu non mới nở - tuổi nhỏ.\r\n- Sử dụng thuốc hoá học khi thật cần thiết. Phun thuốc để phòng trừ khi sâu đa số tuổi 1-3 (lưu ý giai đoạn bắp 3-6 lá), phun vào sáng sớm hoặc chiều mát. Sử dụng các loại thuốc để phun như: Proclaim® 5WG, Match® 050EC, Radiant 60SC, Voliam targo® 063SC,... \r\n1.6. Rệp muội (Aphis maydis).\r\n* Nhận dạng:\r\nRệp trưởng thành cơ thể nhỏ, dài 1,5-1,9 mm, hình bầu dục, màu vàng nhạt, có cánh hoặc không có cánh. Dạng có cánh thường phát sinh nhiều vào cuối vụ hoặc khi mật số quần thể cao để di chuyển đi nơi khác.\r\n* Tập tính sinh sống và cách gây hại:\r\n- Rệp thường bám tập trung thành đám trên lá nõn, trái non, nhất là trên hoa cờ hút nhựa làm lá vàng khô, cây sinh trưởng kém.\r\n- Rầy còn là môi giới truyền bệnh khảm cho cây ngô làm lá bị quăn queo, cây phát triển không bình thường và chết.\r\n- Rệp phát triển nhiều ở các ruộng ngô trồng mật độ dày, thiếu ánh sáng, bón nhiều đạm.\r\n* Biện pháp phòng trừ:\r\n- Trước khi gieo trồng nên làm sạch cỏ chung quanh và ngay trong ruộng ngô để tránh rệp từ các ký chủ phụ bay sang.\r\n- Không nên trồng mật độ dày sẽ tạo ẩm độ thích hợp cho rệp phát triển.\r\n- Nếu mật số rệp ít, không nên áp dụng thuốc vì rệp có nhiều thiên địch (nhóm bắt mồi ăn thịt như bọ rùa, nhện…). Khi mật độ rầy cao có thể sử dụng các loại thuốc như: Gaucho 600FS, Virtako 40WG, Decis 250WG,…\r\n2. BỆNH HẠI\r\n2.1. Bệnh đốm lá:\r\n* Bệnh đốm lá nhỏ (Helminthosporium maydis).\r\n- Triệu chứng: Vết bệnh ban đầu nhỏ như mũi kim màu hơi vàng, sau lớn rộng ra 2 đầu nhọn dạng hình thoi màu nâu hơi xám. Các vết bệnh có thể liên kết lại thành mảng lớn không có hình dạng nhất định. Bệnh có thể hại trên lá, bẹ lá và vỏ trái.\r\n* Bệnh đốm lá lớn (Helminthosporium turcium).\r\n- Triệu chứng: Vết bệnh ban đầu là những đốm nhỏ hình bầu dục, hơi thấm ướt ở phía trên lá, sau vết bệnh lớn dần tạo những vết khô lớn màu nâu hoặc xám bạc không có quầng vàng, vết bệnh cũng xuất hiện trên cả bẹ lá. Các lá dưới thường bị bệnh trước rồi lan dần lên lá phía trên. Khi thời tiết ẩm ướt trên vết bệnh dễ mọc ra một lớp mốc màu đen.\r\n- Điều kiện phát sinh phát triển:\r\n+ Bệnh đốm lá nhỏ phát sinh ngay ở giai đoạn cây còn nhỏ và gây hại kéo dài đến khi thu hoạch.\r\n- Bệnh đốm lá lớn thường phát triển khi ẩm độ cao và nhiệt độ tương đối thấp. Khi bệnh phát sinh vào thời kỳ ngô phun râu và điều kiện ngoại cảnh thích hợp với bệnh, bệnh có thể gây thiệt hại lớn đến năng suất ngô.\r\n- Thường 2 loại bệnh đốm lá nhỏ và đốm lá lớn cùng xuất hiện trên cùng một cây.\r\n- Biện pháp phòng trừ:\r\n+ Trồng mật độ vừa phải, cần chú ý các biện pháp thâm canh để ngô sinh trưởng tốt cũng hạn chế được tác hại của bệnh.\r\n+ Thường xuyên kiểm tra đồng ruộng để phát hiện bệnh sớm. Khi bệnh phát triển mạnh dùng các loại thuốc để phun như: Amistar Top® 325SC, Nativo 750WG, Antracol 70WP,…\r\n2.2. Bệnh đốm nâu (Physoderma maydis)\r\n* Triệu chứng:\r\n- Vết bệnh đầu tiên là những đốm vàng nhỏ xuất hiện rải rác trên phiến lá. Những đốm bệnh trên gân chính có hình hơi tròn hoặc bầu dục màu nâu sậm.\r\n- Ở mắt và lóng thân cũng có những vết bệnh màu nâu sậm. Khi bệnh phát triển mạnh, những vết bệnh này có thể liên kết với nhau làm thối thân và đổ cây.\r\n* Điều kiện phát sinh phát triển: \r\nBệnh phát triển mạnh khi mưa nhiều và nhiệt độ cao, phổ biến trên giống ngô lai.\r\n* Biện pháp phòng trừ: Tương tự bệnh đốm lá.\r\n2.3. Bệnh đốm vằn (Rhizoctonia solani).\r\n* Triệu chứng:\r\n- Bệnh thường xuất hiện trước tiên trên bẹ lá gần mặt đất sau phát triển dần lên lá và vỏ trái.\r\n- Vết bệnh lúc đầu là những đốm màu xanh xám, hình bầu dục, sau lớn dần không có hình dạng nhất định, loang lổ vằn vện như da beo. Vết bệnh có thể bao phủ phần lớn bẹ, phiến lá và vỏ trái. Lá bị khô và gãy gục do phía bẹ và cuống bị hủy hoại. Trên trái, nấm có thể ăn sâu vào phía trong làm trái bị thối. Trời ẩm ướt trên vết bệnh có thể thấy những sợi nấm màu nâu nhạt và hạch nấm tròn nhỏ, màu đen.\r\n* Điều kiện phát sinh phát triển:\r\nBệnh phát triển trong điều kiện mưa nhiều, nhiệt độ và ẩm độ không khí cao, nhất là trên các ruộng ngô trồng dày, bón nhiều đạm. Thường phát triển mạnh sau khi ngô trổ cờ.\r\n* Biện pháp phòng trừ:\r\n- Vệ sinh đồng ruộng để diệt nguồn bệnh và tạo thông thoáng.\r\n- Không trồng dày, bón phân cân đối NPK, tránh bón thừa đạm.\r\n- Thường xuyên kiểm tra đồng ruộng để phát hiện bệnh sớm. Khi bệnh phát triển mạnh dùng các loại thuốc để phun như: Antracol 70WP, Nativo 750WG, Tilt Super 300EC, Amistar Top 325SC,… \r\n2.4. Bệnh rỉ sắt (Puccinia maydis).\r\n* Triệu chứng:\r\n- Bệnh chủ yếu hại trên lá, ngoài ra có thể thấy trên bẹ lá và vỏ trái.\r\n- Vết bệnh đầu tiên là những đốm xanh trong, sau đó chuyển màu vàng và nổi lên trên 2 mặt lá. Bên trong đốm chứa đầy hạ bào tử màu rỉ sắt, khi biểu bì đốm bệnh bị vỡ ra, các hạ bào tử sẽ phát tán lây lan bệnh.\r\n- Bệnh nặng phiến lá gần như che kín bởi các ổ bào tử làm lá khô cháy, cây sinh trưởng kém, năng suất giảm.\r\n* Điều kiện phát sinh phát triển: \r\nẨm độ cao thuận lợi cho bệnh phát triển. Bệnh có thể phát sinh từ khi cây ngô có 3-4 lá đến khi thu hoạch, thường bị nặng trong giai đoạn trổ cờ đến thu hoạch.\r\n* Biện pháp phòng trừ: \r\n- Dùng giống ngô kháng bệnh;\r\n- Không gieo trồng dày, không bón nhiều phân đạm. \r\n- Tăng cường các biện pháp thâm canh để cây sinh trưởng tốt, tăng sức chống bệnh và hạn chế tác hại do bệnh gây ra.\r\n- Thường xuyên kiểm tra đồng ruộng để phát hiện bệnh sớm. Khi bệnh phát triển mạnh dùng các loại thuốc để phun như: Anvil 5SC, Tilt Super 300EC, Amistar Top 325SC, …\r\n2.5. Bệnh bạch tạng (Sclerospora maydis).\r\n* Triệu chứng: \r\n- Cây bắp có thể bị nhiễm bệnh ngay từ khi cây mới có 2-3 lá đến khi cây lớn. Cây bị bệnh sinh trưởng kém, lá hẹp, trên lá có những vết sọc màu vàng nhạt hoặc trắng bạc lan khắp lá làm lá khô, nếu bệnh nặng cây ngô bị chết. Vào sáng sớm mặt dưới lá bệnh có lớp phấn màu trắng.\r\n- Ngoài triệu chứng sọc trắng vàng trên lá, bệnh còn làm các lá dày hơn và xếp chồng lên nhau, làm biến dạng cờ ngô, ra nhiều chồi nách, nếu trái hình thành thì cũng bị teo nhỏ hoặc rất ít hạt.\r\n* Điều kiện phát sinh phát triển:\r\n- Bệnh phát triển mạnh trong điều kiện khí hậu nóng và ẩm, trồng giống nhiễm, ruộng trũng.\r\n- Bệnh thường gây hại nặng trên ngô ăn tươi.\r\n* Biện pháp xử lý:\r\n- Không trồng các giống ngô dễ nhiễm bệnh. Phát hiện sớm cây bệnh, đặc biệt ở giai đoạn cây con, nhổ bỏ tiêu hủy.\r\n- Luân canh ngô với các cây trồng khác như lúa, cây họ cà, rau. \r\n- Trong thời gian sinh trưởng của cây trên đồng ruộng, một số cây con bị bệnh sớm cần nhổ bỏ đem đốt hoặc chôn vùi thật kỹ để tránh lây lan nguồn bệnh.\r\n- Thường xuyên kiểm tra đồng ruộng để phát hiện bệnh sớm. Khi bệnh phát triển mạnh dùng các loại thuốc để phun như: Phytocide 50WP, Insuran 50WG,…";
            }
            else if (s.Equals("Táo"))
            {
                GuideTitle = "SÂU BỆNH HẠI CÂY TÁO VÀ BIỆN PHÁP PHÒNG TRỪ";
                GuideDescription = "1. SÂU HẠI\r\n1.1. Ruồi đục quả Táo: (Bactrocera dorsalis).\r\n* Nhận dạng:\r\n- Con trưởng thành là loài ruồi, dài 7-9 mm, đầu hình bán cầu, mặt trước màu nâu đỏ với sáu chấm màu đen, ngực giữa có 3 vệt màu vàng xếp theo hình chữ U, trong đó có hai vệt dọc ở 2 góc cạnh, bụng tròn và cuối bụng nhọn, chân màu vàng.\r\n- Trứng hình hạt gạo, kích thước 1x0,2 mm, lúc mới đẻ màu vàng sữa khi sắp nở màu vàng nhạt. \r\n- Ấu trùng dạng dòi mới nở có màu trắng sữa và không có chân (rất dễ phân biệt với màu sâu non của sâu đục trái, có màu hồng tím hay màu hồng, đầu màu nâu đen). Chiều dài khoảng 1,5 mm, khi đẫy sức có thể dài 6-8 mm, màu vàng nhạt, miệng có móc. Ấu trùng nằm trong trái đục và ăn phần mềm của trái. Khi phát triển đầy đủ, dòi búng mình rơi xuống đất để hóa nhộng trong đất, thời gian nhộng khoảng 7-12 ngày hoặc dài hơn nếu gặp lạnh. Dòi làm nhộng sâu trong đất khoảng 3-7 cm.\r\n- Nhộng dạng bọc, dài 5-7 mm, nằm trong kén hình trứng lúc đầu có màu vàng nâu chuyển dần sang nâu đỏ để vũ hóa, chiều dài khoảng 0,8-1 cm. Nhộng nằm dưới đất.\r\n- Thành trùng (ruồi trưởng thành) có cơ thể dài 6-9 mm, sải cánh rộng 13 mm, đầu có dạng hình bán cầu, mặt trước màu nâu đỏ với 6 chấm màu đen. Thân màu vàng nâuvới những vân vàng, cánh trong, hình dạng giống nhưng nhỏ hơn ruồi nhà, hoạt động vào ban ngày. Thành trùng hiện diện suốt năm, thời gian sống của thành trùng 1-3 tháng. Thành trùng có thể bay rất xa.\r\n- Điểm phân biệt giữa con đực và con cái dựa vào bộ phận đẻ trứng của con cái kéo dài và nhọn.\r\n* Tập quán sinh sống và cách gây hại:\r\n- Ruồi gây hại quanh năm, ngoài cây Táo ruồi còn gây hại trên nhiều loại cây ăn trái khác như ổi, cam, quýt, đu đủ; mỗi năm 6-7 lứa.\r\n- Ruồi đục trái trên Táo phát triển mạnh vào tháng 5, mật số cao vào tháng 10-11, khi mùa trái cây rộ tập trung vào tháng12-01 (ruồi phát sinh rộ bắt đầu cuối mùa khô và kéo dài cho đến hết mùa mưa).\r\n- Trong điều kiện nhiệt độ 320C và ẩm độ 75%, ruồi trưởng thành có thể sống 20-40 ngày, con cái đẻ 150-200 trứng; giai đoạn ủ trứng 2-3 ngày; giai đoạn ấu trùng 10-18 ngày và giai đoạn nhộng 8-10 ngày.\r\n- Ruồi cái dùng ống đẻ trứng chọc sâu vào vỏ quả rồi đẻ một chùm 5-10 trứng (thường đẻ trứng lên quả phần tiếp giáp giữa vỏ và thịt quả). Vỏ trái nơi ruồi đục vào có màu đen, mềm, ứ nhựa (mủ), tạo điều kiện cho nấm bệnh tấn công làm thối trái và có thể bị rụng hoặc vẫn đeo trên cây.\r\n- Sau khi nở dòi đục ăn phần thịt trái xung quanh chỗ ổ trứng, càng lớn dòi càng đục sâu vào giữa trái làm phần này bị thối và loang dần ra xung quanh, có thể bắt gặp nhiều con dòi sinh sống và gây hại trong cùng một trái Táo. Khi đẫy sức dòi bò ra ngoài rồi chui xuống đất để hóa nhộng.\r\n- Ruồi tấn công lên nhiều loại cây trồng, trái cây bị ruồi đục làm giảm giá trị thương phẩm, chất lượng và không xuất khẩu được. Đối với vườn quất, quýt quả bị hại có màu vàng sáng xung quanh vết châm. Quả bị hại thường thối và rụng. Đối với quả thanh long, ruồi đẻ trứng trên vỏ trái, dòi sau khi nở sẽ sống bên trong trái. Khi đó sẽ thấy bên ngoài vỏ có vết châm kim và ứa nước vàng. Mùa mưa là giai đoạn ruồi sinh sản mạnh và gây hại nặng cho nhà vườn. Có khả năng làm thất thoát >50% năng suất.\r\n* Biện pháp xử lý:\r\n- Vệ sinh đồng ruộng: Thường xuyên thu, hái và đem tiêu hủy toàn bộ trái rụng trên mặt đất và trái còn đeo trên cây vì là nơi ruồi lưu tồn.\r\n- Biện pháp canh tác:\r\n+ Thường xuyên tỉa bỏ, tiêu hủy những cành già, cành tăm, cành bị sâu bệnh,... để vườn luôn được sạch sẽ và thông thoáng;\r\n+ Sau thu hoạch nên làm đất (xới nhẹ), sử dụng bánh neem có trộn phân hữu cơ (2-3 kg/gốc) bón vào gốc (1/3 đường kính tán trong gốc);\r\n+ Tránh trồng xen với những cây đồng ký chủ như ổi, đu đủ, mận,... Vì các chủng loại cây trên có quanh năm và rất mẫn cảm với ruồi đục quả.\r\n- Sử dụng bẫy canh tác: Do cây húng quế hoặc hoặc rau é tía khi ra hoa tiết một mùi rất giống Methyl eugenol và có sự hấp dẫn đến ruồi đục quả. Vì vậy, trồng một góc vườn với cây rau húng quế hoặc rau é tía (Basil) để thu hút ruồi đực và phun thuốc bảo vệ thực vật (dạng tiếp xúc) để diệt chúng.\r\n- Thu hoạch: Thời gian chín thích hợp và thu trên diện rộng, đồng loạt. Lưu ý không để trái chín “neo” quá lâu trên cây.\r\nc. Biện pháp hóa học: Có thể rải Vibam 5GR, Sago-Super 3GR, Nugor 10GR,... rải xung quanh gốc Táo để diệt nhộng đang nằm dưới đất.\r\nLưu ý: Hạn chế dùng thuốc hóa học phun trực tiếp lên trái Táo khi trái sắp thu hoạch vì rất dễ gây ngộ độc cho người tiêu dùng, nếu chưa đến thời kỳ thu hoạch mà bắt buộc phải sử dụng thì phải chú ý bảo đảm thời gian cách ly của thuốc (có ghi trên bao bì hoặc theo hướng dẫn của cơ quan chuyên môn).\r\nd. Đặt bẩy dẫn dụ ruồi trưởng thành: \r\n- Diệt ruồi đực bằng bẫy phê-rô-môn sinh dục (sex pheromones): Thường treo lọ, keo, lon,… gắn miếng gạc thấm chất dẫn dụ ruồi đực Methyl eugenol để thu hút và tiêu diệt ruồi đực. Các chế phẩm thường dùng như: Flykil  95EC, Vizubon D AL, bẫy keo dính vàng,... \r\n- Diệt cả ruồi đực lẫn ruồi cái: Dùng bả mồi (thức ăn) ưa thích của ruồi đục trái là Protein thủy phân, trộn với thuốc trừ sâu để phun lên cây hoặc đặt bẫy bả. \r\n* Để phòng trừ hiệu quả ruồi đục quả Táo nên áp dụng kỹ thuật bao lưới chống ruồi vàng (phụ lục)\r\n1.2. Sâu đục trái Táo: (Conogethes punctiferalis).\r\n* Nhận dạng:\r\n- Con trưởng thành có kích thước nhỏ, chiều dài sải cánh 14-20 mm, chiều dài thân 6 mm, màu nâu. Toàn thân và cánh màu vàng, trên cánh có nhiều chấm đen.\r\n- Ấu trùng dài khoảng 10-22 mm, sâu non có màu hồng hoặc màu hồng tím, đầu nhỏ màu nâu đen, thân màu trắng ửng hồng, hai đốt ngực (trước và giữa) và hai đốt thân ở cuối đuôi thường có màu trắng hơi hồng, các đốt còn lại có màu hồng. Mỗi đốt sống lưng có 4 đốm nâu nhạt, 2 đốm trên to, hai đốm dưới dài và hẹp, trên mỗi đốm đều có lông cứng nhỏ, mỗi đốt sống lưng có một đốm nhỏ màu nâu ở bên hông cơ thể, lổ thở màu đen. Sâu non có 5 tuổi, 3-4 tuần thì hóa nhộng\r\n- Nhộng ban đầu có màu nhạt, lúc sắp vũ hóa chuyển sang màu nâu sậm, dài 6-8 mm, thời gian nhộng kéo dài khoảng 8 ngày. Trứng sâu đục trái hình bầu dục, dài khoảng  2-2,5 mm.\r\n- Con cái hoạt động vào ban đêm, đẻ trứng từ 30-50 quả rải rác trên lá hoặc trên cuống quả. Trứng nở 7-10 ngày. Sau 24 giờ, sâu non đục vào quả non để phá hại.\r\n* Tập quán sinh sống và cách gây hại:\r\n- Con cái hoạt động vào ban đêm, đẻ trứng rải rác ở gần cuống (hoặc trên thân) của những trái còn non. Sau khi nở sâu non đục vỏ trái chui vào bên trong để ăn phá phần thịt trái, đặc biệt chúng rất thích ăn phần hạt và phần thịt trái gần xung quanh hạt. Sâu tấn công và gây hại từ lúc trái còn rất nhỏ (trái bằng ngón tay cái) đến trái lớn, sắp thu hoạch và thiệt hại nặng nhất vào lúc trái sắp thu hoạch. Khi bị sâu hại, trái thường bị thối rất nhanh.\r\n- Tại những lổ đục sâu đùn phân ra ngoài. Nếu gặp nước mưa hay gặp ẩm độ không khí cao, xung quanh lổ đục sẽ bị thối và chuyển dần sang màu nâu đen.\r\n- Vị trí gây hại của sâu đục quả ở nhiều vị trí khác nhau trên quả, đối với một số quả chỗ vết đục trên vỏ trái hơi nổi u, muốn biết đó có phải là đường đục hay không chỉ cần lấy dao mỏng gọt nhẹ lớp vỏ sẽ thấy có đường đục màu nâu tối bên trong.\r\n- Ngoài những lổ đục nhỏ như vậy còn có thể gặp những lổ đục lớn hơn đầu chân nhang, do những con sâu đã lớn tuổi chui ra từ trái khác đục chui vào. Bổ đôi những trái bị hại sẽ thấy có những con sâu non nằm bên trong (có những trái có đến 4, 5 con). Sâu non có màu hồng hoặc hồng tím (có nơi bà con gọi là con sâu hồng), đầu nhỏ màu nâu đen.\r\n- Sâu gây hại từ khi trái còn non cho đến khi thu hoạch. Khi đẫy sức sâu lớn cỡ đầu chân nhang, chui ra ngoài để làm nhộng trong những lá khô xung quanh hoặc nơi tiếp giáp giữa các trái hoặc trên bề mặt trái.\r\n* Biện pháp xử lý:\r\n- Thu hoạch sớm hơn bình thường, không nên “neo” trái chín quá lâu trên cây.\r\n- Thường xuyên thu gom, tiêu hủy những trái bị sâu hại đang còn trên cây và đã rụng xuống đất, để tiêu diệt sâu bên trong trái. Tỉa bỏ những cành già không còn khả năng cho trái, cành tăm nằm khuất bên trong tán lá, vệ sinh vườn sạch sẽ để vườn luôn được thông thoáng.\r\n- Sử dụng một trong các loại thuốc như: Nimbus 6.0EC, Silsau super 3.5EC, Angun 5WG,… phun vào các đợt cây ra trái non. Trước khi phun, đọc kỹ hướng dẫn sử dụng của nhà sản xuất có in sẵn trên bao bì và tuyệt đối phải bảo đảm thời gian cách ly của thuốc trước khi thu hoạch.\r\n1.3. Rệp sáp: (Planococcus lilacinus).\r\n* Nhận dạng:\r\n- Con trưởng thành cái của loài rệp này dài khoảng 2,5-4 mm, chiều ngang cơ thể khoảng 0,7-3 mm. Rìa mỗi bên cơ thể có 18 sợi tua trắng. Cơ thể phủ đầy chất sáp trắng như bông nên có người gọi là rệp sáp phấn, rệp bông hay rệp sáp giả. Chân phát triển, đốt chuyển và đốt đùi chân sau dài 2,10-3,15 mm. Trên đốt chậu và đốt chầy chân sau có nhiều lổ trong.\r\n- Rệp bám từng ổ trên đọt non, mặt dưới lá và chùm hoa. Rệp chích hút nhựa làm lá và chùm hoa xoăn lại, đồng thời có nấm bồ hóng phát triển.\r\n* Tập quán sinh sống và cách gây hại:\r\n- Rệp gây hại khi trái còn non, chích hút trên cuống trái và trái. Thường tập trung với mật số cao trên các chùm trái dày chặt, trong suốt giai đoạn phát triển của trái.\r\n- Trên lá: Rệp sáp chích hút làm lá bị quăn queo.\r\n- Trên trái non nếu mật số của rệp cao sẽ làm cho trái không phát triển được và có thể bị rụng sớm. Nếu mật số rệp thấp hoặc tấn công khi trái đã lớn thì trái vẫn tiếp tục phát triển nhưng ăn không ngon, ăn nhạt, chua.\r\n- Rệp tiết ra mật ngọt tạo môi trường thích hợp cho nấm bồ hóng (Capnodium sp.) phát triển, làm trái bị phủ một lớp bồ hóng, màu đen bẩn, bán không được giá cao, gây thiệt hại cho nhà vườn. \r\n- Loài rệp này ít di chuyển, chúng sống cộng sinh với kiến đen. Bằng cách kiến đen tha rệp từ nơi này sang nơi khác, từ cây này sang cây khác mỗi khi chỗ rệp đang chích hút đã cạn kiệt nhựa. Ngược lại, trong chất bài tiết của rệp có chứa nhiều chất đường mật làm thức ăn cho kiến.\r\n* Biện pháp xử lý:\r\nĐây là một loài đa kí chủ, vì thế việc phòng trừ không phải lúc nào cũng thu được kết quả mong muốn, do chúng thường xuyên có mặt trên những loại cây khác nhau trong vườn. Để phòng trừ rệp nên áp dụng kết hợp nhiều biện pháp sau đây:\r\n- Biện pháp canh tác, cơ học:\r\n+ Không nên trồng với mật độ quá dày để vườn luôn được thông thoáng;\r\n+ Vệ sinh vườn tược thường xuyên, cắt tỉa bỏ những cành bị sâu bệnh, cành nằm khuất trong tán lá,... để vườn luôn thông thoáng. Chăm sóc chu đáo để cây trồng sinh trưởng và phát triển tốt, có sức chống đỡ với rệp;\r\n+ Dọn sạch cỏ rác, lá cây mục tủ ở xung quanh gốc để phá vỡ nơi trú ngụ của kiến.\r\n+ Cắt tỉa cành thông thoáng sau khi thu hoạch. Phun nước bằng vòi phun có áp lực cao.\r\n- Biện pháp hóa học: \r\n+ Nếu thấy xung quanh gốc có nhiều kiến có thể dùng thuốc rải xung quanh gốc để tiêu diệt kiến, hạn chế không cho kiến tha rệp từ cây này sang cây khác;\r\n+ Kiểm tra vườn thường xuyên để phát hiện và phun thuốc diệt trừ rệp kịp thời, nhất là giai đoạn cây đang có đọt non, lá non, bông và trái non bằng một trong các loại thuốc: Mativex 1.5EW, Difluent 25WP, Butyl 40WG, Hello 700WG, Map-Judo 25WP,... Nên phun trực tiếp vào chỗ có rệp bám. Để nâng cao hiệu quả của thuốc, trước khi phun thuốc nên phun bằng nước có pha xà bông để rửa trôi bớt lớp phấn sáp bên ngoài, khi xịt thuốc dễ tiếp xúc với cơ thể của rệp hơn.\r\nNgoài ra, trên Táo còn có các loài sâu hại khác như bọ trĩ, nhện đỏ, rệp muội, bọ cánh cứng gây hại.\r\n2. BỆNH HẠI \r\n2.1. Bệnh phấn trắng: (nấm xám).\r\n* Tác nhân gây bệnh: Do nấm Podosphaeria leucotricha gây hại, nấm bệnh tấn công trên nhiều bộ phận của cây Táo từ hoa, lá đến chồi non, trái non.\r\n* Triệu chứng:\r\n- Trên lá: Ban đầu là những đốm phấn mịn màu trắng hay xám trắng ở mặt dưới của lá, sau đó phát triển rộng dần làm mép lá bị cuốn vào bên trong, xoắn vặn, lá trở nên thô cứng, giòn và dễ rụng.\r\n- Trên chồi non: Nếu bị bệnh chồi non sẽ ngắn lại, đọt bị chùn, chồi non bị chết, các chồi bên phát triển sau đó lại tiếp tục bị bệnh rồi chùn lại, nhìn những cành non này có hình dáng giống như sừng hươu, lớp phấn trắng trên cành non sẽ chuyển dần sang màu nâu, nếu không phòng trị kịp thời cành non có thể bị chết. \r\n- Trên hoa và trái: Hoa bị biến dạng, xoắn vặn rồi khô cháy. Trên trái, bệnh có thể tấn công ngay từ khi trái còn non, làm cho trái không phát triển được, trái nhỏ, màu nâu. Nếu bị bệnh gây hại nặng trái non có thể bị rụng hàng loạt, chỗ bị bệnh trên trái có phủ một lớp phấn màu trắng mịn, trắng xám.\r\nBệnh thường phát triển trên lá non, điều kiện ẩm độ không khí cao (trên 85%) và nhiệt độ thấp (dưới 200C) bệnh phát triển nặng.\r\n* Biện pháp xử lý:\r\n- Không trồng Táo quá dày, thường xuyên tỉa bỏ những cành bị sâu bệnh, cành nằm khuất trong tán lá, cành không có khả năng cho trái, tạo vườn luôn thông thoáng.\r\n- Nên cắt bỏ và đem ra khỏi vườn rồi tiêu hủy những bộ phận đã bị gây hại nặng trước các đợt ra tược non, ra hoa kết trái non, để hạn chế bớt nguồn bệnh ban đầu lây lan sang các bộ phận non mới ra.\r\n- Khi cây đã bị bệnh có thể phun một trong các loại thuốc sau đây: Zintracol 70WP, Alphacol 700WP, Daconil 500SC, Insuran 50WG,… nên phun ngừa một đợt vào trước các thời điểm cây ra tược mới.\r\n2.2. Bệnh thối nâu và héo hoa Táo:\r\n* Tác nhân gây bệnh: Bệnh thối nâu và bệnh héo hoa do nấm Monilinia laxa và Monilinia fructigena gây ra. Hai loại nấm này liên quan rất chặt chẽ và không thể phân biệt được bằng mắt thường.\r\n* Điều kiện phát sinh phát triển:\r\n- Nấm gây hại bắt đầu tại thời điểm nở hoa vào giữa mùa Xuân, nhưng rõ ràng hơn khi trở lại vào cuối mùa Xuân và đầu mùa Hè.\r\n- Trái bị thối nâu, hoa bị teo lại, thường xuất hiện trong điều kiện ẩm ướt.\r\n- Mức độ gây hại nghiêm trọng khác nhau rất nhiều từ năm này sang năm khác, tùy thuộc vào điều kiện thời tiết tại thời điểm ra hoa.\r\n* Biện pháp xử lý:\r\n- Giảm thiểu tác nhân gây bệnh bằng cách loại bỏ tất cả các trái Táo bị mềm nát, có màu nâu kịp thời, không để trái bệnh còn trên cây.\r\n- Bệnh thối nâu lây nhiễm qua các vết thương, đặc biệt là những tác nhân gây ra do các loài chim, các loại nấm có sẵn lây nhiễm sang trái cây khác.\r\n- Khi bệnh xuất hiện có thể phun phòng trừ bằng các loại thuốc sau: Aliette 800WG, Zithane Z 80WP, Insuran 50WG, Daconil 75WP,...\r\n2.3. Bệnh thối trái (do nhiều loại nấm gây ra): \r\n* Tác nhân gây bệnh:\r\nBệnh do nhiều loại nấm và vi khuẩn gây hại. Nhiều nhất là nấm Phytopthora Cactorum và vi khuẩn Erwinia sp. \r\n* Triệu chứng:\r\n- Bệnh thối nhũn làm quả thâm đen úng nước là do vi khuẩn Erwinia (Bacteriaceae) gây hại.\r\n- Bệnh héo quả làm quả nhăn nheo hơi khô, trông như quả bị mất nước nhanh là do nấm Phytophthora cactorum gây hại.\r\n- Trái bị bệnh, không có hình dạng rõ rệt, màu nâu nhạt và ướt nước, thịt trái bên trong bị thối nhũn. Nếu nặng, bệnh có thể lan ra khắp cả trái làm cho trái bị thối và rụng.\r\n* Điều kiện phát sinh phát triển:\r\n- Bệnh thường gây hại nhiều ở những vườn trồng Táo quá dày, bịt bùng thiếu ánh sáng, không thông thoáng, gây ẩm ướt dưới tán của vườn Táo. Nguồn nấm bệnh tồn tại trong những trái Táo bị bệnh đã rụng xuống đất hoặc còn bám trên cây, sau đó sinh bào tử phát tán, lây lan nhờ gió và nước sang các trái khỏe khác.\r\n- Vào những thời điểm có ẩm độ cao, vùng bị bệnh có thể xuất hiện lớp tơ nấm màu trắng.\r\n- Bệnh thường gây hại nhất là vào mùa mưa có ẩm độ không khí cao, gây ẩm ướt. Bệnh tấn công trên những trái già sắp chín, làm thiệt hại nhiều đến năng suất và phẩm chất, gây thất thu cho nhà vườn.\r\n* Biện pháp xử lý: Trái là kết quả cuối cùng của cả vụ chăm sóc, vì vậy nếu chúng ta lơ là giai đoạn mang trái để cho bệnh thối trái tấn công thì coi như thất bại do đó bằng mọi giá phải bảo vệ trái. Khi bệnh xuất hiện nên dùng các loại thuốc gốc đồng như: Insuran 50WG, Geno 2005 2SL, Diboxylin 2SL,… để phòng trừ.\r\n2.4.  Bệnh ghẻ Táo: \r\n* Tác nhân gây bệnh: Do nấm Venturia inaequalis gây hại.\r\n* Triệu chứng:  \r\n- Trên lá: Vết bệnh xuất hiện ở dưới mặt lá, có màu xám xanh, xung quanh có màu đậm hơn, có tơ màu đen hơi xanh, vết bệnh hơi lồi lên. Nếu bị nặng lá sẽ nhỏ lại, phồng lên, khô chết dọc theo bìa lá.\r\n- Trên hoa: Bệnh tấn công trên mầm hoa rất sớm, vết bệnh nhỏ, màu xám, không rõ nét và khó phát hiện. Nếu bị nặng, có thể hoa bị chết khô và rụng.\r\n- Trên trái: Vết bệnh có hình tròn rõ nét, ban đầu có màu xám xanh, có lớp tơ phát triển trên đó. Sau đó vết bệnh chuyển sang màu xanh sậm hơn, nổi lên thành mụn ghẻ và nứt. \r\n- Khi bệnh nặng, trái bị méo mó và rụng sớm. Nếu bệnh tấn công trễ vào cuối vụ thì sau khi thu hái bệnh sẽ tiếp tục phát triển trên trái tạo thành những vết nhỏ như đầu kim, màu đen trên vỏ trái.\r\n* Điều kiện phát sinh phát triển:\r\n- Bệnh thường phát sinh, phát triển mạnh vào những tháng có mưa ẩm kéo dài. Nấm bệnh lưu tồn trên các bộ phận bị bệnh, khi gặp ẩm độ cao sẽ sinh sản bào tử và phát tán theo gió, làm cho bệnh lây lan nhanh.\r\n- Nấm thường tấn công trên phiến lá, cuống lá, hoa và trái non, ít khi tấn công trên chồi non. Các sợi nấm thường lây lan qua các giọt nước, gió và xâm nhiễm vào lổ khí khổng của bộ phận non ở lá, cuống hoa, quả non để phát triển và gây hại.\r\n* Biện pháp xử lý: \r\n- Sau thu hoạch cần đốn tỉa triệt để và thu gom hết tàn dư cây bệnh đem tập trung và đốt hết để tránh lây lan.\r\n- Không trồng quá dày làm vườn cây thiếu ánh sáng; cắt tỉa, tạo hình để cây sinh trưởng phát triển tốt, có độ thông thoáng, tránh được ẩm độ cao trong vườn.\r\n- Để hạn chế bệnh, cần tiến hành các biện pháp phòng trừ, có thể dùng các loại thuốc như: Daconil 75WP, Antracol 70WP,... để phun phòng trừ, ngoài ra nên dùng vôi bột để xử lý đất.\r\n2.5. Bệnh đốm bồ hóng:\r\n* Tác nhân gây bệnh: Do nấm Meliola commixta gây ra.\r\n* Triệu chứng:  \r\n- Bệnh thường chỉ xuất hiện ở mặt dưới của những lá già (nhất là những lá nằm ở dưới thấp), rất ít khi gặp trên lá bánh tẻ và lá non.\r\n- Vết bệnh hình tròn hoặc hơi tròn, màu đen, vết bệnh càng lớn thì màu đen càng sậm hơn. Bề mặt của vết bệnh hơi gồ lên, cao hơn so với mặt lá. Do vết bệnh có màu đen như bồ hóng bếp nên người ta đã gọi nó là bệnh đốm bồ hóng.\r\n* Điều kiện phát sinh phát triển: Bệnh phát sinh quanh năm, nhưng thường phát triển mạnh nhất vào giai đoạn cuối vụ. Quan sát thực tế cho thấy những vườn không được chăm sóc chu đáo, thiếu phân, thiếu nước, vườn ít được cắt tỉa cành, rậm rạp là những vườn thường bị bệnh gây hại nhiều hơn.\r\n* Biện pháp xử lý:\r\n- Không nên trồng quá dày, tạo cho vườn luôn thông thoáng. Thường xuyên tỉa bỏ những cành bị sâu bệnh, khô chết, những cành già ở bên trong tán cây không có khả năng cho trái. Đồng thời phải đốn tỉa cành nhánh định kỳ hàng năm kết hợp với những lần chăm sóc, bón phân, để điều khiển cho cây ra hoa và để vườn Táo luôn được trẻ hóa.\r\n- Chăm sóc vườn, bón phân, tưới nước đầy đủ theo nhu cầu sinh trưởng của cây Táo. Biện pháp này thường mang lại hiệu quả phòng ngừa bệnh rất cao.\r\n- Để hạn chế tác hại của bệnh, có thể sử dụng một trong các loại thuốc như: Melody duo 66.75WP, Insuran 50WG, Manage 5WP, Saizole 5SC,... phun ướt đều cả mặt dưới của lá.";
            }

            IsModalVisible = true;
        }
        private void UpdateButtons()
        {
            Buttons.Clear();
            for (int i = 1; i < Weathers.Count; i++)
            {
                if (Weathers[i][2].ToString()!.Contains(SelectedLocation))
                {
                    Buttons.Add(Weathers[i][4].ToString()!);
                    if (i == 1)
                    {
                        MsnWebUrl = Weathers[i][7].ToString()!;
                    }
                }
            }
        }
        private void UpdateButtonAgris()
        {
            ButtonAgris.Clear();
            ButtonAgris.Add("Nho");
            ButtonAgris.Add("Lúa");
            ButtonAgris.Add("Măng tây");
            ButtonAgris.Add("Hành tím");
            ButtonAgris.Add("Ngô");
            ButtonAgris.Add("Táo");
        }
        public void LoadSettings()
        {
            Locations = GenerateHelper.GetLocalLocations();
            SelectedLocation = Preferences.Get(SettingKeys.LastLocationLocal, Locations.FirstOrDefault()!);
            IsClayLocal = Preferences.Get(SettingKeys.IsClayLocal, false);
            HeightWebView = Preferences.Get(SettingKeys.HeightWebView, 1500);
        }
        private async Task SpeakMessage(string message)
        {
            try
            {
                await TextToSpeech.SpeakAsync(message);
            }
            catch (Exception ex)
            {
                await _toastService.ShowAlert("Thông báo", $"Không thể đọc thông báo vì lỗi: {ex.Message}", "OK");
            }
        }

        public string SelectedLocation
        {
            get => selectedLocation;
            set
            {
                SetProperty(ref selectedLocation, value);
                if (!string.IsNullOrEmpty(value))
                {
                    Preferences.Set(SettingKeys.LastLocationLocal, value);
                }
            }
        }
        public LocalWeather SelectedLocalWeather
        {
            get => selectedLocalWeather; 
            set
            {
                SetProperty(ref selectedLocalWeather, value);                  
            }
        }
        public CurrentLocal SelectedCurrentLocal
        {
            get => selectedCurrentLocal;
            set
            {
                SetProperty(ref selectedCurrentLocal, value);
                if (value != null && !string.IsNullOrEmpty(value.ThoiGian) && SelectedLocation != null)
                {
                    AgricultureTitle = $"NÔNG NGHIỆP NGÀY - {value.ThoiGian}";

                    string s = string.Empty;
                    if (value.Tm != null && value.Tx != null) s = $"Nhiệt độ: {value.Tm} - {value.Tx} °C";
                    else if (value.Tm != null) s = $"Nhiệt độ: {value.Tm} °C";
                    else if (value.Tx != null) s = $"Nhiệt độ: {value.Tx} °C";
                    if (value.DoAm != null) s += $"; Độ ẩm: {value.DoAm} mm";

                    AgricultureDescription = $"{s}";
                    Agricultures = ConvertHelper.NongNghiep(SelectedLocation, value.Tm, value.Tx, value.DoAm);
                }
                else
                {
                    AgricultureTitle = "NÔNG NGHIỆP";
                    AgricultureDescription = "Cần chọn dữ liệu để hiển thị";
                }
            }
        }
        public Agriculture SelectedAgriculture
        {
            get => selectedAgriculture;
            set
            {
                SetProperty(ref selectedAgriculture, value);
                if (value != null)
                {
                    string message = $"{value.Title}\n{value.Content}";
                    Task.Run(async () => await SpeakMessage(message));
                }
            }
        }

        public ICommand CloseModalCommand
        {
            get
            {
                if (closeModalCommand == null)
                {
                    closeModalCommand = new Command(() =>
                    {
                        IsModalVisible = false;
                    });
                }
                return closeModalCommand;
            }
        }
        public List<string> HymetnetData
        {
            get => hymetnetData;
            set
            {
                SetProperty(ref hymetnetData, value);
                if (value != null)
                {
                    TitleHymetnet = value[0].ToUpper();

                    var uri = new Uri(value[1]);
                    if (uri.Equals("data:text/txt;charset=utf-8,"))
                    {
                        ContentHymetnets = new() { new TimeGroup() { TimeInfo = "Không có dữ liệu dông sét" } };
                    }
                    else
                    {
                        string encodedData = uri.AbsoluteUri.Substring(uri.AbsoluteUri.IndexOf(",") + 1);
                        string tempData = encodedData.Replace("+", "__PLUS__");
                        string decodedData = WebUtility.UrlDecode(tempData);

                        decodedData = decodedData.Replace("__PLUS__", "+");
                        var timeGroups = decodedData.Split(new[] { "\n" }, StringSplitOptions.RemoveEmptyEntries);
                        var groupedData = new ObservableCollection<TimeGroup>();
                        TimeGroup currentGroup = null!;
                        foreach (var line in timeGroups)
                        {
                            if (line.Contains(":"))
                            {
                                if (currentGroup != null)
                                {
                                    groupedData.Add(currentGroup);
                                }
                                var timeInfo = Regex.Replace(line.Trim(), @"\s*\(\+\d+\s*phút\)", "");
                                currentGroup = new TimeGroup
                                {
                                    TimeInfo = timeInfo,
                                    Places = new List<Place>()
                                };
                            }
                            else if (currentGroup != null)
                            {
                                var placeInfo = line.Trim().Split(',');
                                if (placeInfo.Length >= 2)
                                {
                                    var name = $"{placeInfo[0].Trim()}, {placeInfo[1].Trim()}";
                                    var coords = placeInfo[2].Trim().Split('-');
                                    if (coords.Length == 2 && double.TryParse(coords[0], out double latitude) && double.TryParse(coords[1], out double longitude))
                                    {
                                        IsClayLocal = Preferences.Get(SettingKeys.IsClayLocal, false);
                                        if (IsClayLocal)
                                        {
                                            if (placeInfo[1].Trim().Contains("Ninh Thuận"))
                                            {
                                                currentGroup.Places!.Add(new Place
                                                {
                                                    Name = name,
                                                    Latitude = latitude,
                                                    Longitude = longitude
                                                });
                                            }
                                        }
                                        else
                                        {
                                            currentGroup.Places!.Add(new Place
                                            {
                                                Name = name,
                                                Latitude = latitude,
                                                Longitude = longitude
                                            });
                                        }
                                    }
                                }
                            }
                        }
                        // Thêm nhóm cuối cùng
                        if (currentGroup != null)
                        {
                            groupedData.Add(currentGroup);
                        }
                        ContentHymetnets = groupedData;
                    }    

                    StatusClay = "Nguồn: Trung tâm mạng lưới KTTV quốc gia";
                }
                else
                {
                    TitleHymetnet = "CẢNH BÁO DÔNG SÉT";
                    ContentHymetnets = new() { new TimeGroup() { TimeInfo = "Không có dữ liệu dông sét" } };
                    StatusClay = "Lỗi khi tải dữ liệu";
                }    
            }
        }
    }
}